control.onControlLoad:
 var companyDetailTypeName= eb.getCookie("companyDetailTypeName");  
 if(companyDetailTypeName=="生产企业"){
     ee.setCheckBoxValue(ee.getControl("checkbox1"), 1);
     ee.setCheckBoxValue(ee.getControl("checkbox2"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox3"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox4"), 0);
 }  
 else if(companyDetailTypeName=="物流"){ 
     ee.setCheckBoxValue(ee.getControl("checkbox1"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox2"), 1);
     ee.setCheckBoxValue(ee.getControl("checkbox3"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox4"), 0);
 }
 else if(companyDetailTypeName=="经销商"){  
     ee.setCheckBoxValue(ee.getControl("checkbox1"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox2"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox3"), 1);
     ee.setCheckBoxValue(ee.getControl("checkbox4"), 0);
 }
 else if(companyDetailTypeName=="终端中心"){  
     ee.setCheckBoxValue(ee.getControl("checkbox1"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox2"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox3"), 0);
     ee.setCheckBoxValue(ee.getControl("checkbox4"), 1);
 }


g5.userId = null;
g5.tableClick = function(sender) {
    var c, x, y;
    for (y = 0; y < g5.pageSize; y++) {
        for (x = 0; x < 5; x++) {
            c = ee.getTableCell(ee.getControl("tablebody2"), x, y);
            c.style.backgroundColor = "";    
        }
    }
    for (x = 0; x < 5; x++) {
        c = ee.getTableCell(ee.getControl("tablebody2"), x, sender.rowIndex);
        c.style.backgroundColor = "#E0EEFF";
    }
    if (eb.isEmpty(g5.dataset1)) {
        g5.userId = null;
        return;
    }
    g5.id = parseInt(g5.dataset1.getData(0, sender.rowIndex));
    g5.companyName = g5.dataset1.getData(1, sender.rowIndex);
    g5.companyTypeName = g5.dataset1.getData(2, sender.rowIndex);
    g5.companyAddress = g5.dataset1.getData(3, sender.rowIndex);  
    g5.companyTypeValue = g5.dataset1.getData(5, sender.rowIndex);
}


//分页处理
g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber < 1) pageNumber = 1;
    if (pageNumber > g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent != pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent > 0) {
            query();
        }
    }
}

query();  

g5.companyTypeValue = 1;
g5.companyTypeName = "生产企业"; 
ee.setText(ee.getControl("panel32"), g5.companyTypeName); 
ee.setText(ee.getControl("panel41"), g5.companyTypeValue);      
//g5.companyTypeValue = null;
//g5.companyTypeName = null; 
g5.table1Click = function(sender) {  
    if (eb.isEmpty(g5.dataset2)) {
        //g5.companyTypeValue = null;
        //g5.companyTypeName = null;
        return;
    }
    g5.companyTypeValue = parseInt(g5.dataset2.getData(1, sender.rowIndex));
    g5.companyTypeName = g5.dataset2.getData(0, sender.rowIndex);
    ee.setText(ee.getControl("panel32"), g5.companyTypeName); 
    ee.setText(ee.getControl("panel41"), g5.companyTypeValue); 
    ee.showAni(ee.getControl("panel33"), 1);
}
ee.getControl("panel33").style.zIndex = 10000;

control.onClick:
if (eb.isEmpty(g5.id) || isNaN(g5.id)) {
    alert("请选择有效的记录进行编辑");
    return;
} 

ee.setText(ee.getControl("panel10"), g5.id);
ee.setEditText(ee.getControl("edit2"), g5.companyName);
ee.setEditText(ee.getControl("edit1"), g5.companyAddress);
ee.setText(ee.getControl("panel32"), g5.companyTypeName); 
ee.setText(ee.getControl("panel41"), g5.companyTypeValue); 
g5.operation = "edit";
ee.showAni(ee.getControl("panel1"));

if (eb.isEmpty(g5.id) || isNaN(g5.id)) {
    alert("请选择有效的记录进行操作");
    return;
}
if (!confirm("删除操作将导致不可返回的后果，如需恢复数据只有联系网管，您是否继续这个危险的操作？")) {
    return;
}
del();

taskSetItem.script2:
var token = eio.readString8();
var searchText = eio.readString8(); 
var check1 = eio.readInt32();
var check2 = eio.readInt32();
var check3 = eio.readInt32();
var check4 = eio.readInt32();
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();

var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
}   
var userId = db.getString(sqlResult, "id");
var companyId = db.getString(sqlResult, "companyId");

//获取记录数量
var condition = "";
if (searchText != "") {
    condition +=
        " and (" +
        "(cast(id as varchar(15)) like '%" + db.encode(searchText) + "%') or " +
        "([companyName] like '%" + db.encode(searchText) + "%') or " +
        "([companyTypeName] like '%" + db.encode(searchText) + "%') or " +  
        "([companyAddress] like '%" + db.encode(searchText) + "%')) ";
}  
var checkstate="";                                   
if(check1==1){
     checkstate+="1,";
} 
if(check2==1){
      checkstate+="2,";
} 
if(check3==1){
        checkstate+="3,";
}
if(check4==1){
        checkstate+="4,";
}
if(checkstate=="") {
        checkstate=" and companyTypeValue in(12) ";
}
else{
    checkstate=" and companyTypeValue in("+ checkstate.substring(0,checkstate.length-1) +") ";
}
sql =
    "SELECT count(*) as c " +
    "FROM companyDetail " +
    "WHERE ([status] <> 'DEL') " + condition +checkstate;
sqlResult = db.executeQuery("DB", sql);   
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

sql= 
    "SELECT * " +
    "FROM companyDetail " +
    "WHERE (status <> 'DEL') " + condition + checkstate+
    "ORDER BY id DESC ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
} 
sql="select  distinct companyTypeValue,companyTypeName from companyDetail order by companyTypeValue";
var sqlResult1 = db.executeQuery("DB", sql); 
if (sqlResult1 == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
} 
eio.appendString8("T");
eio.appendInt32(count);
eio.appendDataSet(sqlResult,"id,companyName,companyTypeName,companyAddress,status,companyTypeValue", pageSize*(pageCurrent-1), pageSize); 
eio.appendDataSet(sqlResult1,"companyTypeName,companyTypeValue", 0, 4);

