//鼠标点击行激活处理
g5.userId = null;
g5.tableClick = function(sender) {
    var c, x, y;
    for (y = 0; y < g5.pageSize; y++) {
        for (x = 0; x < 3; x++) {
            c = ee.getTableCell(ee.getControl("tablebody2"), x, y);
            c.style.backgroundColor = "";    
        }
    }
    for (x = 0; x < 3; x++) {
        c = ee.getTableCell(ee.getControl("tablebody2"), x, sender.rowIndex);
        c.style.backgroundColor = "#E0EEFF";
    }
    if (eb.isEmpty(g5.dataset1)) {
        g5.userId = null;
        return;
    }
    g5.id = parseInt(g5.dataset1.getData(0, sender.rowIndex));
    g5.code1 = g5.dataset1.getData(2, sender.rowIndex);
    g5.code2 = g5.dataset1.getData(3, sender.rowIndex);
    g5.code3 = g5.dataset1.getData(4, sender.rowIndex);
}

//分页处理
g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber < 1) pageNumber = 1;
    if (pageNumber > g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent != pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent > 0) {
            query1();
        }
    }
}

g5.pageSize1 = 15;
g5.pageCurrent1 = 1;
g5.pageCount1 = 1;
g5.pageJump1 = function (pageNumber) {
    if (pageNumber < 1) pageNumber = 1;
    if (pageNumber > g5.pageCount1) {
        pageNumber = g5.pageCount1;
    }
    if (g5.pageCurrent1 != pageNumber) {
        g5.pageCurrent1 = pageNumber;
        if (g5.pageCurrent1 > 0) {
            query();
        }
    }
}


g5.userId1 = null;
g5.tableClick1 = function(sender) {
    var c, x, y;
    for (y = 0; y < g5.pageSize1; y++) {
        for (x = 0; x < 9; x++) {
            c = ee.getTableCell(ee.getControl("tablebody1"), x, y);
            c.style.backgroundColor = "";    
        }
    }
    for (x = 0; x < 9; x++) {
        c = ee.getTableCell(ee.getControl("tablebody1"), x, sender.rowIndex);
        c.style.backgroundColor = "#E0EEFF";
    }
    if (eb.isEmpty(g5.dataset3)) {
        g5.userId1 = null;
        return;
    }
    g5.id1 = parseInt(g5.dataset3.getData(7, sender.rowIndex));
}


