g5.refresh = function() {
    var data=g5.dataset2.data;    
    var tmin=Number(g5.tmin);
    var tmax=Number(g5.tmax);  
    g5.chartoption.xAxis.data=data[0];
    g5.chartoption.visualMap.pieces[0].lte=tmin ; 
    g5.chartoption.visualMap.pieces[1].gt=tmin ;
    g5.chartoption.visualMap.pieces[1].lte=tmax ;
    g5.chartoption.visualMap.pieces[2].gt=tmax ; 
    g5.chartoption.series[0].markLine.data[0].yAxis=tmin; 
    g5.chartoption.series[0].markLine.data[1].yAxis=tmax;
    g5.chartoption.series[0].markArea.data[0][0].yAxis=tmin; 
    g5.chartoption.series[0].markArea.data[0][1].yAxis=tmax;  
    var seriesdata=[];
    for (i = 0; i < data[2].length; i++) { 
        seriesdata.push({
            name: data[3][i],
            value:data[2][i]
        })
    }    
    g5.chartoption.series[0].data=seriesdata;
    g5.chartoption.yAxis.min=function(value) {        
        if(value.min>tmin){
            return  Number((tmin-1)).toFixed(0)
        }else{ 
            return Number(value.min-1).toFixed(0)
        }
    }
    g5.chartoption.yAxis.max=function(value) {        
        if(value.max<tmax){
            return  Number((tmax+1)).toFixed(0)
        }else{ 
            return Number(value.max+1).toFixed(0)
        }
    }

    //console.log(g5.chartoption)
    g5.echart.clear();
    g5.echart.setOption(g5.chartoption);  
}      


function getNowFormatDate() {
    var date = new Date();
    var seperator1 = "-";
    var seperator2 = ":";
    var month = date.getMonth() + 1;
    var strDate = date.getDate();  
    var hour=date.getHours();
    var minutes=date.getMinutes();
    var seconds=date.getSeconds();
    if (month >= 1 &amp;&amp; month <= 9) {
        month = "0" + month;
    }
    if (strDate >= 0 &amp;&amp; strDate <= 9) {
        strDate = "0" + strDate;
    }
    if (hour >= 0 &amp;&amp; hour <= 9) {
        hour = "0" + hour
    }
    if (minutes >= 0 &amp;&amp; minutes <= 9) {
        minutes = "0" + minutes
    }
    if (seconds >= 0 &amp;&amp; seconds <= 9) {
        seconds = "0" + seconds
    }
    var currentdate = date.getFullYear() + seperator1 + month + seperator1 + strDate
            + " " + hour + seperator2 + minutes
            + seperator2 + seconds;
    return currentdate;
}






