var result=[];
var reddataline = [];  
for (i = 0; i < g5.dataset1.data[1].length; i++) { 
    if(g5.dataset1.data[1][i]==""||g5.dataset1.data[1][i]==","||g5.dataset1.data[1][i].indexOf(",")==-1) {}
    else{
        if(parseFloat(g5.dataset1.data[2][i])&amp;&amp;(parseFloat(g5.dataset1.data[2][i])<parseFloat(tmin)||parseFloat(g5.dataset1.data[2][i])>parseFloat(tmax))){
            result.push({"lng":g5.dataset1.data[1][i].split(',')[0],"lat":g5.dataset1.data[1][i].split(',')[1],"red":g5.dataset1.data[2][i]}) 
            if(i==0){}else{ 
                var reddataline1 = [];  
                reddataline1.push(new BMap.Point(g5.dataset1.data[1][i-1].split(',')[0],g5.dataset1.data[1][i-1].split(',')[1]),new BMap.Point(g5.dataset1.data[1][i].split(',')[0],g5.dataset1.data[1][i].split(',')[1]))
                reddataline.push(reddataline1)
            }
        }
        else{
            result.push({"lng":g5.dataset1.data[1][i].split(',')[0],"lat":g5.dataset1.data[1][i].split(',')[1],"blue":g5.dataset1.data[2][i]}) 
        }
        //result.push({"lng":g5.dataset1.data[1][i].split(',')[0],"lat":g5.dataset1.data[1][i].split(',')[1]})
    }
}
for (i = 0; i < g5.dataset2.data[1].length; i++) { 
    if(g5.dataset2.data[1][i]==""||g5.dataset2.data[1][i]==","||g5.dataset2.data[1][i].indexOf(",")==-1) {}
    else{ 
        result.push({"lng":g5.dataset2.data[1][i].split(',')[0],"lat":g5.dataset2.data[1][i].split(',')[1],"remark":g5.dataset2.data[2][i]})
    }
}  

