    function ShowRoute(chartData, viaRouteData) {
        var newChartData = [];
        //对坐标点重新定义
        $.each(chartData, function (item, value) {
            newChartData.push(new BMap.Point(value.lng, value.lat));
        }); 
        //加载地图
        var maps = new BMap.Map(ee.getControl("panel20"), { enableMapClick: false });
        //maps.centerAndZoom(new BMap.Point(118.10000, 24.46667), 15);
        maps.centerAndZoom(new BMap.Point(116.404, 39.915), 15);
        maps.enableScrollWheelZoom(true);
        maps.panTo(newChartData[0]);
        maps.setViewport(newChartData);
        //maps.panTo(newChartData[newChartData.length-1]);
        //把步行线路的坐标集合转化成折线
        var polyline = new BMap.Polyline(newChartData, { strokeColor: "blue", strokeWeight: 3, strokeOpacity: 0.6 });
        maps.addOverlay(polyline); 

        $.each(reddataline,function(item, value){
            var polylinered = new BMap.Polyline(value, { strokeColor: "red", strokeWeight: 3, strokeOpacity: 0.6 });
            maps.addOverlay(polylinered); 
        })   
        //var polylinered = new BMap.Polyline(reddata1, { strokeColor: "red", strokeWeight: 3, strokeOpacity: 0.6 });
        //maps.addOverlay(polylinered); 


        //比例尺 
        var top_left_control = new BMap.ScaleControl({anchor: BMAP_ANCHOR_TOP_LEFT});// 左上角，添加比例尺
        var top_left_navigation = new BMap.NavigationControl();  //左上角，添加默认缩放平移控件
        var top_right_navigation = new BMap.NavigationControl({anchor: BMAP_ANCHOR_TOP_RIGHT, type: BMAP_NAVIGATION_CONTROL_SMALL}); //右上角，仅包含平移和缩放按钮

        maps.addControl(top_left_control);      // 测距离的   
        maps.addControl(top_left_navigation);   // 左导航默认  
        //maps.addControl(top_right_navigation);  // 右导航平移与缩放 

        //超温数据
        $.each(reddata, function (item, value) {  
            //var myIcon = new BMap.Icon("http://api.map.baidu.com/img/markers.png", new BMap.Size(23, 25), {  
            //            offset: new BMap.Size(5, 5) // 指定定位位置    
            //}); 
            var myIcon = new BMap.Icon("./Icon/red.png", new BMap.Size(23, 25), {  
                        offset: new BMap.Size(5, 5) // 指定定位位置    
            });
            var m = new BMap.Marker(value.map,{icon:myIcon});
            var infoWindow = new BMap.InfoWindow(value.remark, { enableMessage: false, width: 30, height: 20 });

            m.addEventListener("mouseover", function () {
                this.openInfoWindow(infoWindow);
            }); 
            m.addEventListener("mouseout", function () {
                this.closeInfoWindow(infoWindow);
            });
            maps.addOverlay(m);
        });


        ////正常数据
        $.each(bluedata, function (item, value) {  
            //var myIcon = new BMap.Icon("http://api.map.baidu.com/img/markers.png", new BMap.Size(23, 25), {  
            //            offset: new BMap.Size(5, 5) // 指定定位位置    
            //}); 
            var myIcon = new BMap.Icon("./Icon/blue.png", new BMap.Size(23, 25), {  
                        offset: new BMap.Size(5, 5) // 指定定位位置    
            });
            var m = new BMap.Marker(value.map,{icon:myIcon});
            var infoWindow = new BMap.InfoWindow(value.remark, { enableMessage: false, width: 30, height: 20 });

            m.addEventListener("mouseover", function () {
                this.openInfoWindow(infoWindow);
            });
            m.addEventListener("mouseout", function () {
                this.closeInfoWindow(infoWindow);
            });
            maps.addOverlay(m);
        });


        //var m1 = new BMap.Marker(newChartData[newChartData.length-1]);
        //maps.clearOverlays(m1);  
        var myIcon1 = new BMap.Icon("./Icon/车.png", new BMap.Size(36, 30), {  
            //offset: new BMap.Size(5, 5) // 指定定位位置    
        });
        var m2 = new BMap.Marker(newChartData[newChartData.length-1],{icon:myIcon1});
        maps.addOverlay(m2);


        //货品流转信息
        $.each(viaRouteData, function (item, value) {  
         var myIcon = new BMap.Icon("http://api.map.baidu.com/img/markers.png", new BMap.Size(23, 25), {  
                        offset: new BMap.Size(10, 10) // 指定定位位置  
                        //imageOffset: new BMap.Size(0, 0 - 10 * 25) // 设置图片偏移  
                    });  

            var m = new BMap.Marker(value.map);
            var infoWindow = new BMap.InfoWindow(value.remark, { enableMessage: false, width: 30, height: 20 });
            //m.addEventListener("click", function () {
            //    this.openInfoWindow(infoWindow);
            //}); 
            m.addEventListener("mouseover", function () {
                this.openInfoWindow(infoWindow);
            }); 
            m.addEventListener("mouseout", function () {
                this.closeInfoWindow(infoWindow);
            });
            maps.addOverlay(m);
            var lab1 = new BMap.Label(value.remark, { position: value.map });//, offset: new BMap.Size(-10, -25)
            //lab1.setStyle({
            //    color: "#fff",
            //    fontSize: "16px",
            //    backgroundColor: "0.05",
            //    border: "0",
            //    fontWeight: "bold"
            //});   
//var marker =new BMap.Marker(point,{icon:pIcon}); // 创建标注点
          //maps.addOverlay(lab1);
        });

    } 


