control.onControlLoad:
//鼠标点击行激活处理
g5.userId = null;
g5.tableClick = function(sender) {
    var c, x, y;
    for (y = 0; y < g5.pageSize; y++) {
        for (x = 0; x < 5; x++) {
            c = ee.getTableCell(ee.getControl("tablebody2"), x, y);
            c.style.backgroundColor = "";    
        }
    }
    for (x = 0; x < 5; x++) {
        c = ee.getTableCell(ee.getControl("tablebody2"), x, sender.rowIndex);
        c.style.backgroundColor = "#E0EEFF";
    }
    if (eb.isEmpty(g5.dataset1)) {
        g5.userId = null;
        return;
    }
    g5.id = parseInt(g5.dataset1.getData(0, sender.rowIndex));
    g5.code1 = g5.dataset1.getData(2, sender.rowIndex);
    g5.code2 = g5.dataset1.getData(3, sender.rowIndex);
    g5.code3 = g5.dataset1.getData(4, sender.rowIndex);
}

//分页处理
g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber < 1) pageNumber = 1;
    if (pageNumber > g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent != pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent > 0) {
            query();
        }
    }
}

g5.init = function() {
    var postData = "<% request.getPostData() %>".trim();
    g5.mode = "common";
    if (postData.length > 10) {
        g5.mode = "json";
        try {
            g5.params = eval("(" + postData + ")");
        } catch (err) {
            g5.mode = "common";
        }
    }
    //识别请求命令
    if (g5.mode == "json") {
        g5.func = g5.params.func;
    } else {
        g5.func = ee.getQueryString("func");
    }
    if (eb.isEmpty(g5.func)) {
        //可能是普通页面调用
        return;
    }
    if (g5.func.toLowerCase() == "queryrfid") {
    } else if (g5.func.toLowerCase() == "queryrqr") {
    } else if (g5.func.toLowerCase() == "queryrsn") {
    } else if (g5.func == "") {
        return;
    } else {
        alert("接收到未知指令：" + g5.func);
        return;
    }
    //获取请求参数
    g5.func = g5.func.toLowerCase();
    if (g5.mode == "json") {
        g5.sessionId = g5.params.sessionId;
        g5.code = g5.params.code;
    } else {
        g5.sessionId = ee.getQueryString("sessionId");
        g5.code = ee.getQueryString("code");
    }

    //开始查询unionCode
    query();
}

setTimeout(function() {
    g5.init();
}, 200);

taskSetItem.script2:
var token = eio.readString8();
var searchText = "";
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var func = eio.readString16();
var code = eio.readString16();

var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
}   
var userId = db.getString(sqlResult, "id");
var companyId = db.getString(sqlResult, "companyId");

//获取unionCode
func = func.toLowerCase();
if (func == "queryrfid") {
    sql = "select * from unionCode where codeEpc='" + db.encode(code) + "'";
} else if (func == "queryrqr") {
    sql = "select * from unionCode where codeQr='" + db.encode(code) + "'";
} else if (func == "queryrsn") {
    sql = "select * from unionCode where codeBar='" + db.encode(code) + "'";
} else {
    eio.appendString8("F");
    eio.appendString8("未知命令" + func);
    return;
}
sqlResult = db.executeQuery("DB", sql);   
if (!db.read(sqlResult)) {
    eio.appendString8("F");
    eio.appendString8("代码不存在：" + code);
    return;
}
var unionCode = db.getString(sqlResult, "id");

//获取记录数量
var condition = "";
if (searchText != "") {
    condition +=
        " and (" +
        "(cast(id as varchar(15)) like '%" + db.encode(searchText) + "%') or " +
        "([vhcType] like '%" + db.encode(searchText) + "%') or " +
        "([plateNumber] like '%" + db.encode(searchText) + "%') or " +
        "([note] like '%" + db.encode(searchText) + "%')) ";
}
sql =
    "SELECT count(*) as c " +
    "FROM [log] " +
    "WHERE (intParam2=" + unionCode + ") " + condition;
sqlResult = db.executeQuery("DB", sql);   
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

sql= 
    "SELECT id, right(left(CONVERT(varchar(100), regDatetime, 20),16),11) as regDatetime, note " +
    "FROM [log] " +
    "WHERE (intParam2=" + unionCode + ") " + condition + " " +
    "ORDER BY id DESC ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("数据库执行错误");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");
eio.appendInt32(count);
eio.appendDataSet(sqlResult,"regDatetime,note", pageSize*(pageCurrent-1), pageSize);

taskSetItem.script3:
if (eio.readString8() != "T") {
    alert("无法获取您需要的数据：" + eio.readString8());
    return;
}
g5.pageRecordCount = eio.readInt32();
g5.dataset1 = eio.readDataSet();
