//任务栏处理
g5.setCell = function (cell, skinIndex, text1, text2, text3, text4, text5, text6) {
    if (skinIndex!=cell.skinIndex) {
        cell.skinIndex = skinIndex;
        if (skinIndex == 1) {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "/App1PJ/cMain/task1.png", "fill");    
        } else if (skinIndex == 2) {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "/App1PJ/cMain/task2.png", "fill");    
        } else if (skinIndex == 3) {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "/App1PJ/cMain/task3.png", "fill");    
        } else {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "url:res/empty.png", "fill");
        }
    } 
    if (ee.getText(cell.eeChildren[0].eeChildren[1]) != text1) {
        ee.setText(cell.eeChildren[0].eeChildren[1], text1);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[2]) != text2) {
        ee.setText(cell.eeChildren[0].eeChildren[2], text2);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[3]) != text3) {
        ee.setText(cell.eeChildren[0].eeChildren[3], text3);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[4]) != text4) {
        ee.setText(cell.eeChildren[0].eeChildren[4], text4);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[5]) != text5) {
        ee.setText(cell.eeChildren[0].eeChildren[5], text5);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[6]) != text6) {
        ee.setText(cell.eeChildren[0].eeChildren[6], text6);
    }    
}       


g5.taskRefresh = function() {
    var i, cell;
    if (eb.isEmpty(g5.task)) {
        for (i=0; i<6; i++) {
            cell = ee.getTableCell(ee.getControl("tablebody1"), 0, i);
            g5.setCell(cell, 0, "", "", "", "", "", "");
        }
        setTimeout(g5.taskRefresh, 100);
        return;
    }          
    var task1 = 0, task2 = 0;
    var mainTime2 = -1, mainTime3 = -1;
    for (i=0; i<6; i++) {
        cell = ee.getTableCell(ee.getControl("tablebody1"), 0, i);
        if (i>=g5.task.length) {
            g5.setCell(cell, 0, "", "", "", "", "", "");
            continue;    
        }    
        var text1 = "", text2 = "", text3 = "", text4 = "", text5 = "", text6 = "";
        var skinIndex = 0;
        var time1 = new Date(g5.task[i][4].replace(/-/g,"/"));
        var time2 = new Date(g5.task[i][5].replace(/-/g,"/")); 
        var time3= g5.task[i][5].substring(5,16);
        cell.taskId = parseInt(g5.task[i][0]);
        cell.mobile = g5.task[i][7]; 

        if (g5.task[i][1]=="1new") { 
            task1++;
            skinIndex = 1;
            time1 = time1.getTime() - (new Date()).getTime();
            text1 = "姓名：" + g5.task[i][12];
            if (time1<0) {
                text4= "预约<font color='#47a9f9'>" + g5.task[i][6] + "</font>小时"+ " " +"<font color='#47a9f9'>已过期</font>";
            } else {
                text4 = "预约<font color='#47a9f9'>" + g5.task[i][6] + "</font>小时"+ " " +"<font color='#47a9f9'>" + g5.timeToString(time1) + "</font>后过期";
            }
            if (g5.task[i][3].lenght<=0) {
                text2 += "地址：未填写";
            } else {
                text2 += "地址：" + g5.task[i][3];
            }
            text5 +="";// "<font color='#47a9f9'>" + g5.task[i][8] + "车</font>"+ " " +"预约<font color='#47a9f9'>" + g5.task[i][6] + "</font>小时";
            text3 += "时间：<font color='#47a9f9'>" + time3 + "</font>" ;// g5.timeToString2(time2)
             //+"预约<font color='#47a9f9'>" + g5.task[i][6] + "</font>小时";
            text6 +="<font color='#47a9f9'>" + g5.task[i][8] + "车</font>"+ " " + g5.task[i][9]+" "+"￥" + g5.task[i][10];
            cell.note = text1 + "；</br>" + text2+"；</br>" + text3 + "；</br>" + text6 + "；</br>留言："+g5.task[i][11]+ "；"; 
        } else if (g5.task[i][1]=="2met") {    
            task2++;
            skinIndex = 2;
            var time2diff = time2.getTime() - (new Date()).getTime();
            if (mainTime2==-1) {
                mainTime2 = time2diff;
            } else if (time2diff<mainTime2) {
                mainTime2 = time2diff;
            }  
            text1 += "姓名：" + g5.task[i][12];
            if (time2diff<0) {
                text4 ="预约" + g5.task[i][6] + "小时"+" "+"已过<font color='#d9432b'>" + g5.timeToString(-time2diff) + "</font>";
            } else {
                text4 ="预约" + g5.task[i][6] + "小时"+" "+"还剩<font color='#d9432b'>" + g5.timeToString(time2diff) + "</font>";
            }
            if (g5.task[i][3].lenght<=0) {
                text2 += "地址：未填写";
            } else {
                text2 += "地址：" + g5.task[i][3];
            }    
            text5 +="" ;//g5.task[i][8] + "车"+" "+"预约" + g5.task[i][6] + "小时";
            text3 += "时间：<font color='#d9432b'>" + time3 + "</font>　　";
               // "预约" + g5.task[i][6] + "小时";
            text6 +=g5.task[i][8] + "车"+" "+ g5.task[i][9]+" "+"￥" + g5.task[i][10];
            cell.note = text1 + "；</br>" + text2 + "；</br>"+ text3 + "；</br>" + text6 + "；</br>留言："+g5.task[i][11]+ "；";
        } else if (g5.task[i][1]=="3prc") { 
            skinIndex = 3;
            var time1diff = time1.getTime() - (new Date()).getTime();
            mainTime3 = time1diff;
            text1 += "姓名：" + g5.task[i][12];
            if (time1diff<0) {
                text4 ="课时<font color='#dea63a'>" + g5.task[i][6] + "</font>小时"+" "+ "课程超时<font color='#dea63a'>" + g5.timeToString(-time1diff) + "</font>";
            } else {
                text4 = "课时<font color='#dea63a'>" + g5.task[i][6] + "</font>小时"+" "+"下课还剩<font color='#dea63a'>" + g5.timeToString(time1diff) + "</font>";
            }
            if (g5.task[i][3].lenght<=0) {
                text2 += "地址：未填写";
            } else {
                text2 += "地址：" + g5.task[i][3];
            }
            text5 +="";// g5.task[i][8] + "车" + "课时<font color='#dea63a'>" + g5.task[i][6] + "</font>小时";
            text3 += "时间：" + time3 + "　　";
               //+ "课时<font color='#dea63a'>" + g5.task[i][6] + "</font>小时";
            text6 +=g5.task[i][8] + "车"+" "+ g5.task[i][9]+" "+"￥" + g5.task[i][10];  
        }
        g5.setCell(cell, skinIndex, text1, text2, text3, text4, text5, text6);   
        //return time3;
    }
    //刷新首页信息
    text = "";
    if (task1>0) {
        text += "<font color='#eb6100'>" + task1 + "</font>" + "个未接订单<br/>";
    }
    if (task2>0) {
        text += "<font color='#eb6100'>" + task2 + "</font>" + "个预约订单";
        if (mainTime2<0) {
            text += "　已过<font color='#eb6100'>" + g5.timeToString(-mainTime2) + "</font></br>";
        } else {
            text += "　还剩<font color='#eb6100'>" + g5.timeToString(mainTime2) + "</font></br>";
        }
    }        
    if (mainTime3!=-1) {
        if (mainTime3<0) {
            text += "正在陪驾　已超时<font color='#eb6100'>" + g5.timeToString(mainTime3) + "</font></br>";
        } else {
            text += "正在陪驾　<font color='#eb6100'>" + g5.timeToString(mainTime3) + "</font>后结束</br>";
        }
    }
    if (ee.getText(ee.getControl("panel7"))!=text) {
        ee.setText(ee.getControl("panel7"), text);
    }
    ee.setVisible(ee.getControl("panel20"), text!="");
    setTimeout(g5.taskRefresh, 100);
}
g5.timeToString = function(ms) {
    ms = parseInt(ms/1000);
    var result = "";
    if (ms<0) {
        result += "-";
        ms = Math.abs(ms);
    }
    if (ms>=3600) {
        result += parseInt(ms/3600) + "小时";    
    }
    if (ms>=60) {
        result += parseInt((ms%3600)/60) + "分";
    }                                           
    result += parseInt(ms%60) + "秒";
    return result;
}
g5.timeToString2 = function(time) {
   var text = "";
    if (time.getHours()<10) text += "0";
   text += time.getHours() + ":";
    if (time.getMinutes()<10) text += "0"
    text += time.getMinutes();
    return text;    
}
