taskSetItem.script2:
var newsType = eio.readInt32(); 
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var sql, sqlResult;  
while (true) {
     sql = 
        "select cast(id as nvarchar) + '`' + title as idTitle, " +
        "convert(nvarchar, regDatetime, 20) as time " +
        "from news where newsType = " + newsType +
        " order by regDatetime desc";
     sqlResult = db.executeQuery("PJ", sql);
    if (sqlResult == null) {
        eio.appendString8("F");
        eio.appendString8("系统繁忙，请稍后再试" + sql + newsType);
        break;
    }
    eio.appendString8("T");
    eio.appendDataSet(sqlResult, "idTitle,time",pageSize*(pageCurrent-1), pageSize);
    break;
}  

//获取记录数量
sql = "select count(*) as c from news where newsType='"+newsType+"'";// + newsType;
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c"); 
eio.appendInt32(count);

taskSetItem.script3:
if (eio.readString8()!="T") {
    alert("获取文章列表失败：" + eio.readString8());
    return;
}
g5.newsList = eio.readDataSet();
g5.pageRecordCount = eio.readInt32(); 
