ee.setText(ee.getControl("layoutxitem8"), g5.pageCurrent + "/" + g5.pageCount);

taskSetItem.script2:
var token = eio.readString8();
var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
} 
var userId = db.getString(sqlResult, "id");

var taskId = eio.readInt32();
sql = "exec taskAccept " + userId + "," + taskId; 
sqlResult = db.executeQuery("PJ", sql);
if (!db.read(sqlResult)) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙，请稍后再试");
    return;
}
if (db.getInt32(sqlResult, "errorCode") != 0) {
    eio.appendString8("F");
    eio.appendString8(db.getString(sqlResult, "errorString"));
    return;
}
eio.appendString8("T");

