    ee.setImage(ee.getControl("panel51"),"/App1PJ/evaluation/coacheval/star3.png","fill","",""); 

}
var textT="训练次数："+panel.times;
var textH="陪练课时："+panel.hour;

ee.setText(ee.getControl("panel53"),textT);
ee.setText(ee.getControl("panel55"),textH);

//alert(this.rowIndex);   
var panel = ee.getTableCell(ee.getControl("tablebody1"), 2, this.rowIndex);  
//alert(panel.times); 
//alert(panel.hour); 
var lv=panel.lv;     


var rIndex=this.rowIndex; 
//项目描述 
if(rIndex==0){
    text="每辆车的主要功能按钮分布大致相同，熟悉这些功能，并知道操作后，观察车内车外呈现的形势。" 
    ee.setText(ee.getControl("panel45"),text);
}   
if(rIndex==1){
    text="左手9点钟位置，右手3点钟位置。</br>用食指到小指和手掌自然地握住方向盘，拇指自然地按住方向盘，注意自然、轻松握住。" 
    ee.setText(ee.getControl("panel45"),text);
}  
if(rIndex==2){
    text="提速提档，减速减档，操作过程中不低头看档位，换挡即时，不拖档。" 
    ee.setText(ee.getControl("panel45"),text);
}   
if(rIndex==3){
    text="两侧视角基本镜像对称（调节：上下天地各占1/2，左右车身占镜面1/4）</br>学会使用反光镜和倒车镜判断车道、车距，行驶中时不时去主动观察。" 
    ee.setText(ee.getControl("panel45"),text);
}   
if(rIndex==4){
    text="视线平视前方、视角成扇形、望远顾近，方向盘微调让车辆始终行驶在车道中间，不忽左忽右晃动。" 
    ee.setText(ee.getControl("panel45"),text);
}  
if(rIndex==5){
    text="提前观察是否需要并线变道进入转向车道，转弯三部曲：1开转向灯，2观察反光镜判断是否安全，3看着要转入的车道逐渐调整方向盘角度。遇到路口有待转等候区域，观察信号灯，直行绿灯时可上前，同时开转向灯。" 
    ee.setText(ee.getControl("panel45"),text);
}   
if(rIndex==6){
    text="注意前方两侧多瞭望，通过路口提前20米将右脚放回刹车踏板预防事故，在车速不快时仅仅放回刹车踏板即可，无需踩下减速。通过路口停车线后，在安全可控的前提下可以继续提速。" 
    ee.setText(ee.getControl("panel45"),text);
}   
if(rIndex==7){
    text="变道之前必须打转向灯，观察前方及侧后方车辆，确保安全车距，缓打方向，迅速变道。" 
    ee.setText(ee.getControl("panel45"),text);
}  
if(rIndex==8){
    text="对车身宽度判断准确，无面对面会车情况走窄路中间位置。</br>遇会车时，右脚主动回刹车踏板预防，必要时减速。" 
    ee.setText(ee.getControl("panel45"),text);
}  
if(rIndex==9){
    text="确认安全，用最少的时间完成2进1退（如路宽很窄3进2退也可）。要领：方向盘操作手势推荐大把交替法，每次退没进都往相反的方向打满方向，过程中注意多观察来往车辆行人。" 
    ee.setText(ee.getControl("panel45"),text);
}   
if(rIndex==10){
    text="在比较宽的马路掉头时，要先打左向灯，进入左转车道，变道前要观察要进入车道的情况，然后打方向变道、减速，在停车线处车速降至几乎停下的状态，注意来往车辆，等确认安全后方向打到底掉头，掉头后进入的车道仍是靠近中心黄线的那条，然后再打右灯变进自己的车道。" 
    ee.setText(ee.getControl("panel45"),text);
}  
if(rIndex==11){
    text="小路在路口掉头时，需要向右借一点，具体的操作是：打左向灯，减速至五公里左右，观察来往车辆确认安全，人至停车线或进路口时，向右打方向，然后向左打方向到底，小路掉头尤其要注意路况，是否有坑、障碍物，或者停有自行车等，根据实际情况判断需要向右借多少。" 
    ee.setText(ee.getControl("panel45"),text);
}
if(rIndex==12){
    text="厂门/小区口掉头也就是小路掉头，要借助一边的空地，整个过程同样要密切注意来往车辆，确认安全，若是在左侧则打左向灯，减速，靠右走，观察左侧厂门的中线，人过中线，方向打到底掉头;若是在右侧则打左向灯，减速，靠右走，人与空地边缘齐向右推或者打一圈，至人越过路边后，向左打方向到底掉头。" 
    ee.setText(ee.getControl("panel45"),text);
}   
if(rIndex==13){
    text="一般都是能一把掉过去的，但如果没操作好，一把没过去，可停下，向右打方向倒车，留出距离后二次掉头，此时多主动观察两侧及后方车辆。" 
    ee.setText(ee.getControl("panel45"),text);
}  
if(rIndex==14){
    text="此项目，需陪练员在训练过程总不断主动告知前后左右车辆的实际距离，让学员通过车内不同视角去逐渐适应，尤其是通过左右后视镜判断两侧及后方车距。" 
    ee.setText(ee.getControl("panel45"),text);
} 
if(rIndex==15){
    text="训练中，路上有很多交通标志、信号灯、地上划的辅助线，陪练员需主动告诉学员作用，让学员逐渐了解，并在驾驶过程中养成主动去看交通信号、标志的习惯。" 
    ee.setText(ee.getControl("panel45"),text);
}
if(rIndex==16){
    text="手动挡：抬离合器至半联动后稳住车辆，松开刹车后立刻轻轻并持续平稳加一点油门，慢慢抬起离合器。（坡度抖时可以配合手刹）</br>自动挡：松刹车后立即轻轻并持续平稳加一点油门。" 
    ee.setText(ee.getControl("panel45"),text);
}
if(rIndex==17){
    text="难点：判断车身左侧距离取卡机杆距离</br>建议新手按照教学标准流程操作，1距离路边/取卡机一臂距离，2空挡手刹，3取卡/索票，4等停车杆完全升起后再向前开。" 
    ee.setText(ee.getControl("panel45"),text);
}
if(rIndex==18){
    text="要点：车辆与前车平行，两车侧面相距0.5米，当后视镜与前车B柱对齐时，向内侧打满轮开始倒车，后行至与车辆与前车成45度夹角时，反向打满轮继续倒车，入位后头正回轮，完成入库动作。" 
    ee.setText(ee.getControl("panel45"),text);
}
if(rIndex==19){
    text="要点：当车距旁车1.5米，驾驶席对其隔位车中央时，向内侧打满轮倒车，车身摆正后回轮继续倒车，完成入库动作。" 
    ee.setText(ee.getControl("panel45"),text);
}


