//计算进度条百分比
g5.allLV = 20 * 3;
g5.end = g5.getLV / g5.allLV;

var end = g5.end;

g5.end = end.toFixed(2); 
g5.end *= 100;

//alert(g5.end);
//g5.end = "0.9";
var showP = function(now){ 
    now = parseInt(now);
    ee.setText(ee.getControl("panel116"),now+"%"); 
    //console.log (now+"%"); 
    g5.setProgress(now)    
}


g5.now = 0;
var e = g5.end * 0.1;  //增加量

var timeout = false;
function time(){
    if (timeout) return;
    if (g5.end == 0){
        showP(g5.end);
        return;
    }
    g5.now =parseInt(g5.now) + parseFloat(e);
    if (g5.now > g5.end){
        g5.now = g5.end;
        timeout = true;
    }
    showP(g5.now);
    setTimeout(time,10);
} 
time();




