var token = eio.readString8();
var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试");
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("上次登录时间太久或者您更换了系统");
    es.logCommon("用户token登录失败,token=" + token);
    return;
}          
var uid=db.getString(sqlResult,"id"); 

sql="select * from userInfo where id="+uid;  
sqlResult = db.executeQuery("PJ", sql);   
es.logWarning(sql);
db.read(sqlResult);  
var mobile=db.getString(sqlResult, "mobile"); 
var photo1=db.getString(sqlResult, "photo1");   
eio.appendString8(mobile); 
eio.appendString8(photo1); 
eio.appendString8(uid);

