eio.readString8();

g5.dataH = eio.readDataSet();
//console.log(g5.dataA); 
g5.taskH = g5.dataH.data;  

if(g5.taskH[0]== ""){
    g5.rowCountH = 0;
}else{
  g5.rowCountH = g5.dataH.rowCount; 
}

console.log(g5.dataH); 
console.log(g5.taskH);
console.log(g5.rowCountH);   

g5.test= "defang"; 



g5.taskRefresh = function() { 

    console.log("task="+g5.taskH);console.log("count="+g5.rowCountH); console.log("test="+g5.text);
    var status;
    // alert("count="+g5.rowCountH); 
     for(var i=0;i< g5.rowCountH;i++){
         var y = i*140 
          g5.create(y,565,i) 

     }

    for(i=0;i< g5.rowCountH; i++){ 
     var text1 = "", text2 = "", text3 = "", text4 = "", text5 = "", text6 = "", text7="";
        cell = ee.getControl("panel14").eeChildren[i];
        var dateTime = g5.taskH[2][i];
        var index = dateTime.indexOf(" ");
        var date = dateTime.substr(0,index);            
        var time = dateTime.substring(index+1); 
        var remark = g5.taskH[4][i]; 
        var address = g5.taskH[0][i];
        var assess = g5.taskH[3][i];
        if(remark.length > 17){
          remark = remark.substr(0,17) +"...";   
        } 
        if(address.length >7){
          address = address.substr(0,8)+"...";
        }
       // alert(date);alert(time);
        text1 += "陪驾地址: "+address;
        text2 += "课时: "+g5.taskH[1][i]+" 小时"+"  "+time;
        text3 += "陪驾详情:"; //shijian 
        text4 += "教练留言:"+remark;
        //text5 += "已完成"; 
        if(assess ==""){
           text5 += '<span style="color:#ffad59">未评价<span>';  
        }else {
           text5 += '<span style="color:#ffad59">已完成<span>';
        }

        text6 += date; 
        //alert(assess);
        var index1 = assess.indexOf(";");
        text7 = assess.substr(0,index1);
       // alert(text7)


        g5.setCell(cell,text1,text2,text3,text4,text5,text6,text7);

    }        
    //setTimeout(g5.taskRefresh, 3000);
}

  g5.taskRefresh(); 
//setTimeout(g5.taskRefresh,2000);

