control.onPageCreate:
var visited = eb.getCookie("visited");
g5.token = eb.getCookie("pjToken");  

var url = window.location.search; 
var index = url.indexOf("&");   
var from = "";
if (index != -1){
    from = url.substring(index+1);
}  

if (from == "weChat" || from =="wechat"){
    eb.setCookie("from","weChat");
} else {

    if (eb.isEmpty(visited) && eb.isEmpty(g5.token)){      
        eb.setCookie("visited","T");     
        window.location.href = ee.pageRoot + "/slide";
    }else {}
}

taskSetItem.script2:
var token = eio.readString8();
var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试");
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    //eio.appendString8("上次登录时间太久或者您更换了系统");  
    eio.appendString8("请重新登录");
    es.logCommon("用户token登录失败,token=" + token);
    return;
}
var userType = db.getString(sqlResult, "userType")          
var applyS=db.getString(sqlResult,"autoStatus"); 

/*var autoStatus = db.getString(sqlResult, "autoStatus"); 
var userType = db.getString(sqlResult, "userType");
if(autoStatus=="CNN" &amp;&amp; userType=="教练"){
   eio.appendString8("F2"); 
   return;
}*/  

eio.appendString8("T");
eio.appendString8(db.getString(sqlResult, "userType"));   
eio.appendString8(applyS);
es.logCommon("用户token登录,nickName=" + db.getString(sqlResult,"nickName")+
    ",mobile=" + db.getString(sqlResult, "mobile"));
