control.onControlLoad:
g5.setCookie = function (key, value, expHours) {
    var exp = new Date();
    if (expHours == undefined) {
        expHours = 20 * 365 * 24;
    }
    exp.setTime(exp.getTime() + expHours*60*60*1000);
    document.cookie = key + "="+ escape (value) + ";expires=" + exp.toGMTString();     
}
g5.getCookie = function (key) {
    var arr, reg = new RegExp("(^| )" + key + "=([^;]*)(;|$)");
    if(arr = document.cookie.match(reg)) 
        return unescape(arr[2]);
    else return null;
}
g5.delCookie = function (key) {
    g5.setCookie(key,"",0);
}

