control.onControlLoad:
var isIOS = !!navigator.userAgent.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/);
if (!isIOS) {
    ee.getControl("layoutyitem1").heavy = 0;
}
refreshCard();

g5.bodyClick = function(x,y) {
    var cell = ee.getTableCell(ee.getControl("tablebody1"), 1,y);
    if (ee.getText(cell).length<=0) return;
    ee.setText(ee.getControl("panel24"), "持卡人姓名：" + ee.getText(ee.getTableCell(ee.getControl("tablebody1"), 0, y)));
    ee.setText(ee.getControl("panel25"), "银行卡卡号：" + ee.getText(ee.getTableCell(ee.getControl("tablebody1"), 1, y)));
    ee.setText(ee.getControl("panel26"), "开户行：" + ee.getText(ee.getTableCell(ee.getControl("tablebody1"), 2, y)));
    ee.setText(ee.getControl("panel27"), "添加时间：" + ee.getText(ee.getTableCell(ee.getControl("tablebody1"), 3, y)));
    //alert(ee.getTableCell(ee.getControl("tablebody1"), 1, y).cardId);
    g5.deleteCardId = parseInt(ee.getTableCell(ee.getControl("tablebody1"), 1, y).cardId);
    ee.showAni(ee.getControl("panel17"));    

}
setTimeout(function(){
    ee.autoResize = false;
},1000);

control.onUpdate:
if (text.length<6) {
    ee.setText(sender,"");
    return;
}
var a = text.split(",");
text = a[1];
sender.cardId = a[0];
var newText = text.substring(0,4) + "****" + text.substring(text.length-4);
ee.setText(sender,newText);

control.onClick:
ee.setText(ee.getControl("panel35"), ee.getText(ee.getControl("panel24")));
ee.setText(ee.getControl("panel36"), ee.getText(ee.getControl("panel25")));
ee.setText(ee.getControl("panel37"), ee.getText(ee.getControl("panel26")));
ee.setText(ee.getControl("panel38"), ee.getText(ee.getControl("panel27")));
ee.setVisible(ee.getControl("panel17"), false);
ee.showAni(ee.getControl("panel29"));

taskSetItem.script1:
//客户端验证
g5.userName = ee.getEditText(ee.getControl("edit1"));
if ((g5.userName.length<2)||(g5.userName.length>10)) {
    alert("请输入一个真实有效的名字");
    return;
}
g5.bankCardNumber = ee.getEditText(ee.getControl("edit2"));
if ((g5.bankCardNumber.length<7)||(g5.bankCardNumber.length>20)) {
    alert("请输入一个有效的银行卡卡号");
    return;
}
g5.bankName = ee.getEditText(ee.getControl("edit3"));
if ((g5.bankName.length<0)) {
    alert("请输入有效的开户行，开户行文字错误会导致后续的提现操作失败");
    return;
}
eio.appendString8(eb.getCookie("pjToken"));
eio.appendString8(g5.userName);
eio.appendString8(g5.bankCardNumber);
eio.appendString8(g5.bankName);

taskSetItem.script2:
var token = eio.readString8();
var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
} 
var userId = db.getString(sqlResult, "id");

var userName = eio.readString8();
var bankCardNumber = eio.readString8();
var bankName = eio.readString8();
//查询银行卡是否已经存在
sql = "select * from bankCard where userId = " + userId +
    " and cardNumber = '" + db.encode(bankCardNumber) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试");
    return;
}
if (db.read(sqlResult)) {
    eio.appendString8("F");
    eio.appendString8("您输入的银行卡已经存在");
    return;
}
//插入
sql = "insert into bankCard (cardUserName,cardNumber,bankName,userId) values (" +
    "'" + db.encode(userName) + "'," +   
    "'" + db.encode(bankCardNumber) + "'," +   
    "'" + db.encode(bankName) + "'," +
    userId + ")";
if (!db.executeNoneQuery("PJ", sql)) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试");
    return;
}
eio.appendString8("T");

taskSetItem.script3:
if (eio.readString8()!="T") {
    alert("添加银行卡失败：" + eio.readString8());
    return;
}
refreshCard();

taskSetItem.script2:
var token = eio.readString8();
var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
} 
var userId = db.getString(sqlResult, "id");

sql = "select cardUserName,cast(id as varchar(20))+','+cardNumber as cn,bankName, " +
    "cast(datepart(year, regdatetime) as varchar(20)) + '年' + " +
    "cast(datepart(month,regdatetime) as varchar(20)) + '月' as reg " +
    "from bankCard where userId = " + userId;
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试"+sql);
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"cardUserName,cn,bankName,reg",0,1000);

