function myArrToStr(arr){
    var str="";
    var tmp="";
    for(var i=0;i<arr.length;i++){
        if(i!=0){
            tmp =",";
        }else{
            tmp='';
        }
        if(arr[i]<10){
            tmp += "0"+arr[i];

        }else {
            tmp +=arr[i];
        }

        str+=tmp;
    }
    return str;

}
