control.onControlLoad:
function is_weixn(){ 
    var ua = navigator.userAgent.toLowerCase(); 
    if(ua.match(/MicroMessenger/i)=="micromessenger") { 
        //cosnole.log("true"); 
        ee.setText(ee.getControl("panel1"),"true");
        return true;
    } else {             
        //cosnole.log("false");
        ee.setText(ee.getControl("panel1"),"false");
        return false; 
    } 
}
is_weixn();
