control.onControlLoad:
g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber<1) pageNumber = 1;
    if (pageNumber>g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent!=pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent>0) {
            getInfo();
        }
    }
}     
　//ee.setText(ee.getControl("panel44"),"123<br />456")

g5.money;
g5.moneyFree;
g5.dateTime; 
g5.taskID;
g5.card;
g5.name; 
g5.lastMoney;
g5.lastMoneyFree;
//任务栏点击
g5.taskClick = function(cell){    
    var cell = ee.getTableCell(ee.getControl("tablebody1"), 0, cell.rowIndex);
    var cellText=ee.getTableCellText(ee.getControl("tablebody1"), 0, cell.rowIndex); 
    g5.money=ee.getTableCellText(ee.getControl("tablebody1"), 2, cell.rowIndex);
    g5.moneyFree=ee.getTableCellText(ee.getControl("tablebody1"), 3, cell.rowIndex);
    g5.dateTime=ee.getTableCellText(ee.getControl("tablebody1"), 1, cell.rowIndex);

    var panel = ee.getTableCell(ee.getControl("tablebody1"), 3, cell.rowIndex);//lastMoney  

    g5.taskID=panel.taskId;  
    g5.lastMoney=panel.lastMoney;
    g5.lastMoneyFree=panel.lastMoneyFree;

    var texts;  

    if(cellText.length<=0||cellText==""){  
        return;
    }
    if(cellText=="提交订单"){ 
       getOrder();

    }   
    if(cellText=="充值成功"){   
       texts="充值完成时间："+panel.time+"<br />充值金额："+g5.money+"元<br />赠送金额："+g5.moneyFree+"元<br />充值方式："+panel.type+"<br />充值后账户余额："+g5.lastMoney/100;
       ee.setText(ee.getControl("panel1"),texts) 
       ee.showAni(ee.getControl("panel43")); 
    }   
    if(cellText=="提现申请"){   
       applyCash();

    }
    if(cellText=="提现失败"){   
       failCash();
    }
    if(cellText=="取消订单"){  
       delOrder();
    }
    if(cellText=="订单完成"){  
       completeOrder();
    }              
} 
getInfo();

control.onUpdate:
if(text.length > 0){
    if (text == '财开单') {
        text = '提交订单';
        ee.setText(sender, text);
        return;
    }
    if (text == '财订单取消1') {
        text = '取消订单';
        ee.setText(sender, text);
        return;
    }
    if (text == '财订单完成') {
        text = '订单完成';
        ee.setText(sender, text);
        return;
    }
    if (text == '财提现') {
        text = '提现申请';
        ee.setText(sender, text);
        return;
    }
    if (text == '财提现失败') {
        text = '提现失败';
        ee.setText(sender, text);
        return;
    }
    if (text == '财充值') {
        text = '充值成功';
        ee.setText(sender, text);
        return;
    }  
    if (text == '财提成') {
        text = '提成';
        ee.setText(sender, text);
        return;
    }
}else {
    text = ""; 
    ee.setText(sender,text);
}

if(text.length > 0){
  var data = text.split("`");

   //ee.setText(sender, data[0]);
   sender.free = data[0]; 
   sender.time= data[1]; 
   sender.lastMoney= data[5]; 
   sender.lastMoneyFree= data[6];
   sender.classHour= data[7];
   sender.type= data[8];

   sender.taskId= data[4];
   ee.setText(sender,data[5]/100);
}else {
    text = ''; 
    ee.setText(sender,text);
}

