//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "  where ([log].userId="+userId+") and ([log].logType='财开单' or [log].logType='财订单取消1' or [log].logType='财订单完成' or [log].logType='财提现' or [log].logType='财提现失败' or [log].logType='财充值' or [log].logType='财提成') ";
var sql1, sqlResult1;    

//获取记录数量
sql1 = "select count(*) as c from log left outer join userInfo on log.userId = userInfo.id "+condition; 
es.logWarning(sql1);
sqlResult1 = db.executeQuery("PJ", sql1);
db.read(sqlResult1);
var count = db.getInt32(sqlResult1, "c");
//ISNULL(photo7,'')+'`'+ISNULL(licenseNumber,'')+'`'+ISNULL(carId,'')+'`'+ISNULL(carType,'')+'`'+ISNULL(address,'')+'`'+ISNULL(serviceArea,'') as data
//读取表 
sql1 =
    "select userInfo.balance,[log].id,[log].regDatetime,[log].logType,[log].userId, [log].logType,[log].intParam2,[log].strParam2,cast([userInfo].balance as decimal(18,2)) as b,cast([userInfo].balanceFree as decimal(18,2)) as bf," +
    "ISNULL(cast([log].intParam3 as varchar),'')+'`'+ISNULL(cast([log].dtParam2 as varchar),'')+'`'+ISNULL([log].note,'')+'`'+ISNULL([userInfo].nickName,'')+'`'+ISNULL(cast([log].intParam1 as varchar),'')+'`'+ISNULL(cast([log].intParam4 as varchar),'')+'`'+ISNULL(cast([log].intParam5 as varchar),'')+'`'+ISNULL([log].strParam1,'')+'`'+ISNULL([log].strParam2,'')+'`'+ISNULL([log].strParam3,'')+'`'+ISNULL([userInfo].idName,'')+'`'+ISNULL([userInfo].mobile,'') as data " +
    "from [log] LEFT OUTER JOIN userInfo ON [log].userId = userInfo.id " + condition + " order by log.id desc ";  //" + condition + "
   //[log].logType='充值'     " +  condition + "


var sqlResult1 = db.executeQuery("PJ", sql1); 
es.logWarning(sql1);
eio.appendInt32(count);
eio.appendDataSet(sqlResult1,"logType,regDatetime,intParam2,data", pageSize*(pageCurrent-1), pageSize);
db.read(sqlResult1);
var balance = db.getString(sqlResult1, "balance"); 
eio.appendString8(balance);

