var licenseNumber = eio.readString8(); 
var carBand=eio.readString8();   
var coachNumber=eio.readString8();
var id = session.get("mobile"); 
var nickName = session.get("nickName");
var password = session.get("password");


var sql,sqlResult; 

//创建用户
sql = "exec createUser '" + db.encode(id) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试");
    return;
}

db.read(sqlResult);
if (db.getInt32(sqlResult, "errorCode")!=0) {
    eio.appendString8("F");
    eio.appendString8(db.getString(sqlResult, "errorString"));
    return;
}

sql = "update userInfo set " +
    "nickName = '" + db.encode(nickName) + "', " +
    "password = '" + es.getMd5(password) + "', " + 
    "idName = '" + db.encode(idName) + "', " +
    "idNumber = '" + db.encode(idNumber) + "', " +
    "address = '" + db.encode(address) + "', " + 
    "emName = '" + db.encode(emName) + "', " + 
    "emTel = '" + db.encode(emTel) + "', " + 
    "carId = '" + db.encode(carId) + "', " + 
    "carType = '" + db.encode(carType) + "', " + 
    "licenseStart = '" + licenseStart + "', " +
    "serviceArea = '" + db.encode(serviceArea) + "', " + 
    "licenseNumber = '" + db.encode(licenseNumber) + "', " +
    "carBand = '" + db.encode(carBand) + "', " +
    "coachNumber = '" + db.encode(coachNumber) + "', " +
    "userType = '教练',autoStatus='CNN'" +
    "where mobile = '" + db.encode(id) + "'and enabled='T'";
if (!db.executeNoneQuery("PJ", sql)) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试");
    return;
}
