taskSetItem.script3:
var result = eio.readString8(); 
var vehicle = eio.readInt32(); 
var classHour = eio.readInt32();
var money=0;  
var moneyAll=0;
if (result=="F") {
    alert("预约陪驾失败：" + eio.readString8());
    return;
}  
if(result<=0){
    alert("您已经没有可以使用的免费金额");
    ee.setText(ee.getControl("panel116"),"无免费金额");
    return;
}   
var str = result.substring(0,result.indexOf(".") + 3);
var a = confirm("您剩余免费金额为"+str+"元，是否使用免费金额？");
if(a==true){  
    if(vehicle==1){
        moneyAll=100*classHour;        
    }
    else {
        moneyAll=150*classHour;
    }  
    if(moneyAll==200){
        money=20;
    } 
    if(moneyAll==300){
        money=35;
    }
    if(moneyAll==400){
        money=50;
    }
    if(moneyAll==450){
        money=55;
    }
    if(moneyAll==600){
        money=90;
    }
    if(result>=money){
        g5.freeNum=1;   
        ee.setText(ee.getControl("panel116"),"可使用免费金额"+money+"元");  
    }
    else if(result>0&amp;&amp;result<money){
        g5.freeNum=1;   
        ee.setText(ee.getControl("panel116"),"可使用免费金额"+result+"元");
    }  
} 
else {
    g5.freeNum=0;
    ee.setText(ee.getControl("panel116"),"不使用免费金额");
}

