//任务栏点击
g5.taskClick = function (y) {    
    var cell = ee.getTableCell(ee.getControl("tablebody1"), 0, y);
    if (cell==null) return;
    if (cell.skinIndex == 1) {
        g5.currentTaskId = cell.taskId;
        ee.setText(ee.getControl("panel65"), cell.note);
        ee.showAni(ee.getControl("panel56"));     
    } else if (cell.skinIndex == 2) {
        g5.currentTaskId = cell.taskId;
        g5.currentMobile = cell.mobile; 
        ee.setText(ee.getControl("panel50"), cell.note); 
        ee.getControl("panel50").style.overflow="auto";
        ee.showAni(ee.getControl("panel43"));    
    } else if (cell.skinIndex == 3) {
        g5.currentTaskId = cell.taskId;
        g5.currentMobile = cell.mobile; 
        ee.setText(ee.getControl("panel50"), cell.note);
        ee.getControl("panel50").style.overflow="auto";
        ee.showAni(ee.getControl("panel43"));     
    } else if (cell.skinIndex ==4 ){
        g5.currentTaskId = cell.taskId;
        g5.currentMobile = cell.mobile; 
        ee.setText(ee.getControl("panel50"), cell.note);
        ee.getControl("panel50").style.overflow="auto";
        ee.showAni(ee.getControl("panel43"));
    }                           
    ee.setText(ee.getControl("panel51"),"<a href='tel:"+g5.currentMobile+"' trget='_blank'>　　　　　　　　　　　　　</a>");//拨打电话  
    ee.getControl("panel51").style.textDecoration="none";
} 

//任务栏处理
g5.setCell = function (cell, skinIndex, text1, text2, text3, text4, text5, text6) {  
    if(text1>3){
        text1=text1.substr(0,3)+"...";
    }  
    if(text2.length>19){  
        text2=text2.substr(0,19)+"...";   
    } 
    if (skinIndex!=cell.skinIndex) {
        cell.skinIndex = skinIndex;
        if (skinIndex == 1) {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "/App1PJ/cMain/task1.gif", "fill");    
        } else if (skinIndex == 2) {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "/App1PJ/cMain/task2.gif", "fill");    
        } else if (skinIndex == 3) {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "/App1PJ/cMain/task3.gif", "fill");    
        } else if (skinIndex == 4){
            ee.setImage(cell.eeChildren[0].eeChildren[0], "/App1PJ/cMain/task4.gif", "fill");
        } else {
            ee.setImage(cell.eeChildren[0].eeChildren[0], "url:res/empty.png", "fill");
        }
    } 
    if (ee.getText(cell.eeChildren[0].eeChildren[1]) != text1) {
        ee.setText(cell.eeChildren[0].eeChildren[1], text1);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[2]) != text2) {
        ee.setText(cell.eeChildren[0].eeChildren[2], text2);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[3]) != text3) {
        ee.setText(cell.eeChildren[0].eeChildren[3], text3);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[4]) != text4) {
        ee.setText(cell.eeChildren[0].eeChildren[4], text4);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[5]) != text5) {
        ee.setText(cell.eeChildren[0].eeChildren[5], text5);
    }    
    if (ee.getText(cell.eeChildren[0].eeChildren[6]) != text6) {
        ee.setText(cell.eeChildren[0].eeChildren[6], text6);
    }    
}

g5.timeToString = function(ms) {
    ms = parseInt(ms/1000);
    var result = "";
    if (ms<0) {
        result += "-";
        ms = Math.abs(ms);
    }
    if (ms>=3600) {
        result += parseInt(ms/3600) + "小时";    
    }
    if (ms>=60) {
        result += parseInt((ms%3600)/60) + "分";
    }                                           
    result += parseInt(ms%60) + "秒";
    return result;
}
g5.timeToString2 = function(time) {
    var text = "";
    if (time.getHours()<10) text += "0";
    text += time.getHours() + ":";
    if (time.getMinutes()<10) text += "0"
    text += time.getMinutes();
    return text;    
}
