g5.createTitle4 = function(index, title, time) {
    time = time.substring(0,4)+"年"+parseInt(time.substring(5,7))+"月"+parseInt(time.substring(8,10))+"日";
    c = createPanel(g5.root, "temp", "FFFFFF", 0, 572, 490, 41);
    c.bookIndex = index;
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0;
    c.widthRatio=1;
    c.heightRatio=0.0560875512995896;
    c.topRatio=g5.currentPos;
    g5.currentPos+=c.heightRatio;
    var root = c;
    c = createPanel(root, "temp", "E0E0E0", 0, 39, 490, 1);
    eb.setDock(c,"fill","bottom");
