taskSetItem.script2:
var sql,sqlResult;
var token = eio.readString8();
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    es.logWarning("用户token失效, sql=" + sql);
    return;
} 
var userId = db.getString(sqlResult,"id")
//es.logWarning(userId);
sql = 
    "SELECT cast(task.id as nvarchar) + '`' + task.status + '`' + " +
    "  cast(task.studentId as nvarchar) + '`' + task.address + '`' + " + 
    "  convert(nvarchar, task.time1, 20) + '`' + " + 
    "  convert(nvarchar, task.time2, 20) + '`' + " +
    "  cast(task.classHour as nvarchar) + '`' + " + 
    "  task.studentMobile + '`' + task.provideCar + '`'+ task.carStall + '`' + " +
    "  cast(task.pay as nvarchar) + '`' + " + 
    "  task.message + '`' + ISNULL(userInfo.nickName,'') + " +
    " '`' + ISNULL(userInfo.mobile,'') as data " +
    "FROM task LEFT OUTER JOIN " +
    "  userInfo ON task.coachId = userInfo.id " +
    "where task.studentId='" + db.encode(userId) + "' and task.status!='5cms' and task.status!='6del'" +
    "order by task.status, task.id";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙请稍后再试");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult, "data", 0, 1000);

