//news click
g5.newsClick = function (cell) {
    cell = ee.getTableCell(ee.getControl("tablebody2"), 0, cell.rowIndex);
    if (cell.msgId<0) return;
    g5.msgId = cell.msgId;
    getNewsText();   
    ee.setVisible(ee.getControl("layoutx3"),false);
}

//任务栏点击    
var panelTeles;  
g5.taskClick = function (y) {  
    eb.setCookie("keepTask",y);
    var cell = ee.getTableCell(ee.getControl("tablebody1"), 0, y);  
    cell = cell.eeChildren[0];
    //ee.setText(ee.getControl("panel75"),"123");
    if (cell==null){return;}
    if (cell.skinIndex == 1) { 
        g5.currentTaskId = cell.taskId;
        g5.currentMobile = cell.mobile; 
        ee.setText(ee.getControl("panel50"), cell.note);
        ee.getControl("panel50").style.overflow="auto";
        ee.showAni(ee.getControl("panel43"));
        panelTeles=ee.getControl("panel75");  
        //ee.setVisible(ee.getControl("panel51"),false);
        //ee.setText(panelTeles,"<a href='tel:"+g5.currentMobile+"' trget='_blank'>　　　　　　　　　　　　　</a>");    
    } else if (cell.skinIndex == 2) {
        g5.currentTaskId = cell.taskId;
        g5.currentMobile = cell.mobile; 
        ee.setText(ee.getControl("panel58"), cell.note);
        ee.getControl("panel58").style.overflow="auto";
        ee.showAni(ee.getControl("panel51"));
        panelTeles=ee.getControl("panel74"); 
        //ee.setText(panelTeles,"<a href='tel:"+g5.currentMobile+"' trget='_blank'>dddd</a>");    
    } else if (cell.skinIndex == 3) {
        g5.currentTaskId = cell.taskId;
        g5.currentMobile = cell.mobile;
        ee.showAni(ee.getControl("panel60"));
        panelTeles=ee.getControl("panel76");  
        //ee.setText(panelTeles,"<a href='tel:"+g5.currentMobile+"' trget='_blank'>　　　　　　　　　　　　　</a>");   
    } else if (cell.skinIndex == 4){
        g5.currentTaskId = cell.taskId;
        g5.currentMobile = cell.mobile; 
        ee.setText(ee.getControl("panel135"), cell.note);
        ee.getControl("panel135").style.overflow="auto";
        ee.showAni(ee.getControl("panel128"));
        panelTeles=ee.getControl("panel137");
    }                               
    ee.setText(panelTeles,"<a href='tel:"+g5.currentMobile+"' trget='_blank' style='text-decoration:none;'>　　　　　　　　　　　　　</a>");//trget='_blank' 
    //g5.content=""
    //ee.setText(ee.getControl("panel70"),time3)
    panelTeles.style.textDecoration="none"; //去下划线  

} 


//任务栏处理
g5.setCell = function (cell, skinIndex, text1, text2, text3, text4, text5, text6, text7) {  
    if(text1>3){
        text1=text1.substr(0,3)+"...";
    }  
    if(text2.length>19){  
        text2=text2.substr(0,19)+"...";   
    }  
    //console.log("yes");
    if (skinIndex!=cell.skinIndex) {
        cell.skinIndex = skinIndex;


        if (skinIndex == 1) {
            ee.setImage(cell.eeChildren[0], "/App1PJ/cMain/task1.gif", "fill");  

        } else if (skinIndex == 2) {
            ee.setImage(cell.eeChildren[0], "/App1PJ/cMain/task2.gif", "fill"); 

        } else if (skinIndex == 3) {
            ee.setImage(cell.eeChildren[0], "/App1PJ/cMain/task3.gif", "fill");  

        } else if (skinIndex == 4) {
            ee.setImage(cell.eeChildren[0], "/App1PJ/cMain/task4.gif", "fill");           

        } else {
            ee.setImage(cell.eeChildren[0], "url:res/empty.png", "fill");
        }
    } 
    if (ee.getText(cell.eeChildren[1]) != text1) {
        ee.setText(cell.eeChildren[1], text1);
    }    
    if (ee.getText(cell.eeChildren[2]) != text2) {
        ee.setText(cell.eeChildren[2], text2);
    }    
    if (ee.getText(cell.eeChildren[3]) != text3) {
        ee.setText(cell.eeChildren[3], text3);
    }    
    if (ee.getText(cell.eeChildren[4]) != text4) {
        ee.setText(cell.eeChildren[4], text4);
    }    
    if (ee.getText(cell.eeChildren[5]) != text5) {
        ee.setText(cell.eeChildren[5], text5);
    }    
    if (ee.getText(cell.eeChildren[6]) != text6) {
        ee.setText(cell.eeChildren[6], text6);
    }


    /*   
    if(text7 =="查看学员")｛
        ee.setImage(cell.eeChildren[1],"/App1PJ/cMain/viewS.png","fill");
    ｝*/ 

   ee.setVisible(ee.getControl("panel18"),false);

}       

g5.timeToString = function(ms) {
    ms = parseInt(ms/1000);
    var result = "";
    if (ms<0) {
        result += "-";
        ms = Math.abs(ms);
    }
    if (ms>=3600) {
        result += parseInt(ms/3600) + "小时";    
    }
    if (ms>=60) {
        result += parseInt((ms%3600)/60) + "分";
    }                                           
    result += parseInt(ms%60) + "秒";
    return result;
}
g5.timeToString2 = function(time) {
   var text = "";
    if (time.getHours()<10) text += "0";
   text += time.getHours() + ":";
    if (time.getMinutes()<10) text += "0"
    text += time.getMinutes();
    return text;    
}


//左右滑动功能
g5.slideP1 = ee.getControl("layouty1");
g5.slideP2 = ee.getControl("panel79");
g5.slideP3 = ee.getControl("panel17");
g5.slideCurrentPos = 1;
ee.setVisible(g5.slideP1, true);
g5.slideTo = function(slidePos) {
    if (g5.slideCurrentPos == slidePos) return;
    if (g5.slideCurrentPos == 1) {
        if (slidePos == 3) {
            g5._slideInit(g5.slideP1);
            g5._slideInit(g5.slideP3);    
            g5.slideP1.aniEndX = -g5.slideP1.offsetWidth;
            g5.slideP1.aniAutoHide = true;
            g5.slideP3.aniStartX = g5.slideP3.parentNode.offsetWidth;
            g5.slideCurrentPos = slidePos;
        } else if (slidePos == 2) {
            g5._slideInit(g5.slideP1);
            g5._slideInit(g5.slideP2);    
            g5.slideP1.aniEndX = g5.slideP1.parentNode.offsetWidth;
            g5.slideP1.aniAutoHide = true;
            g5.slideP2.aniStartX = -g5.slideP2.parentNode.offsetWidth;
            g5.slideCurrentPos = slidePos;
        }
    } else if ((g5.slideCurrentPos == 3) &amp;&amp; (slidePos == 1)) {
        g5._slideInit(g5.slideP1);
        g5._slideInit(g5.slideP3);    
        g5.slideP1.aniStartX = -g5.slideP1.offsetWidth;
        g5.slideP3.aniEndX = g5.slideP3.parentNode.offsetWidth;
        g5.slideP3.aniAutoHide = true;
        g5.slideCurrentPos = slidePos;
    } else if ((g5.slideCurrentPos == 2) &amp;&amp; (slidePos == 1)) {
        g5._slideInit(g5.slideP1);
        g5._slideInit(g5.slideP2);    
        g5.slideP1.aniStartX = g5.slideP1.parentNode.offsetWidth;
        g5.slideP2.aniEndX = -g5.slideP2.offsetWidth;
        g5.slideP2.aniAutoHide = true;
        g5.slideCurrentPos = slidePos;
    }
    console.log("slidePos="+slidePos);
    //new add code  by richkey
    if(slidePos==3){
        g5.flagVisibleTo =2;
    }else {
        g5.flagVisibleTo =0;
    }
    console.log("flag="+g5.flagVisibleTo);

}
g5._slideInit = function (p) {
    eb.setAni(p, null, "rightFlyIn", null, 0, false, false); 
    ee.setVisible(p, true);
    p.aniStartX = 0;
    p.aniStartY = 0;
    p.aniEndX = 0;
    p.aniEndY = 0;
    ee.showAni(p);
    p.aniStart = true;
    p.aniTime = 300;
}                


