control.onPageCreate:
var url = window.location.search;
console.log(url);                       
var arr = url.split("&");
var str_dType = arr[1];
var str_sName = arr[2];
var index = str_dType.indexOf("=");
var index2 = str_sName.indexOf("=");
var dType = str_dType.substring(index + 1);
var sName = str_sName.substring(index2 + 1);
if (dType != ""){
    eb.setCookie("draw_type",dType);
}else {
    eb.setCookie("draw_type","");
} 
if (sName != ""){
    eb.setCookie("school_name",sName);
}else {
    eb.setCookie("school_name","");
}


window.location.href="https://open.weixin.qq.com/connect/oauth2/authorize?appid=wxeeb9b2f6e1f01d23&response_type=code&redirect_uri=http%3A%2F%2Fm.1peijia.com%2Feengine%2Ffujiandraw.htm&scope=snsapi_base&state=STATE#wechat_redirect";
