var showDay=function(month){ 
    if(month==2){
        ee.setVisible(ee.getControl("panel88"), false);  
        ee.setVisible(ee.getControl("panel87"), false); 
        dayResidue=29-dayTime;         
    }  
    else if(month==4||month==6||month==9||month==11){
        ee.setVisible(ee.getControl("panel88"), false);  
        ee.setVisible(ee.getControl("panel87"), true); 
        dayResidue=30-dayTime;
    } 
    else {
        ee.setVisible(ee.getControl("panel88"), true);  
        ee.setVisible(ee.getControl("panel87"), true);
        dayResidue=31-dayTime;
    }
} 
