ee.getControl("panel67").onclick=function(){
    var div_fu=ee.getControl("panel67");
    var fu_item=div_fu.childNodes.length;  
    for(var i=0;i<fu_item;i++){           
        div_fu.childNodes[i].onclick=function(){ 
            timeDay=ee.getText(this); 
            if(ee.getText(this)=="今天"){
               timeDay=dayTime-0;
              // alert(timeDay); 
            }
            lastTime2=lastTime+timeDay*100;//下个月的时间ee.getText(this)
            if(flags==0){      
                if(ee.getText(this)<dayTime-1){
                    alert("您的预约时间已经过了，请预约至少一个小时后的时间")
                    return;
                }
                lastTime2=lastTime+(timeDay-dayTime)*100;//这个月的时间
            }        
            //this.style.backgroundColor="blue";
            //daySh=     
            dayNum=ee.getText(this);  
            if(ee.getText(this)=="今天"){
                dayNum=dayTime-0;
            }          
            //alert(lastTime2);
            //alert(dayNum);
            //ee.setVisible(ee.getControl("panel10"), false);
            ee.showAni(ee.getControl("panel19")); 
            ee.setVisible(ee.getControl("panel88"), false);  
            ee.setVisible(ee.getControl("panel87"), false);
        }
    } 
    //alert(dayNum);
}  

