            function countTimeLength(interval, date1, date2) {  
                var objInterval = {'D' : 1000 * 60 * 60 * 24, 'H' : 1000 * 60 * 60, 'M' : 1000 * 60, 'S' : 1000, 'T' : 1};  
                interval = interval.toUpperCase();  
                var dt1 = Date.parse(date1.replace(/-/g, "/"));  
                var dt2 = Date.parse(date2.replace(/-/g, "/"));  
                try{  
                    return ((dt2 - dt1) / objInterval[interval]).toFixed(0);//保留两位小数点  
                }catch (e){  
                    return e.message;  
                }  
            } 

            var result = countTimeLength("D",start,end);  

            if(result ==0){
                var a = confirm("您预约的是今天订单，可能来不及派单，请电话联系客服，400-026-9090"); 
                if (!a) {
                    return;
                }

            }  
            //alert(result);
 if(result<0 || result>31){  //result 可以是负值，所以只要判断result值就能够限定范围，当前限定 1-31天
     alert("请您选择 未来31天之内的时间段进行预约");  
     return;
 }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 

g5.gotResult = result;                                                                              

var text = g5.r.getSelectText()+"-"+g5.r1.getSelectText()+"-"+g5.r2.getSelectText();   　
ee.setText(ee.getControl("layoutxitem22"),text); 

ee.setVisible(ee.getControl("panel126"),true);
ee.setVisible(ee.getControl("panel127"),true); 
