//手册功能函数
//激活某类消息
g5.refreshBook = function(index, contentIndex) {
    if (!g5.book1) return;
    var scroll = g5.root.scrollTop;
    var i;
    g5.bookIndex = index;
    g5.contentIndex = contentIndex;
    g5.clearList();
    g5.create1pj();
    if (index==0) {
        g5.create1Active();
        for (i=0; i<g5.book1.rowCount; i++) {
            g5.createTitle4(i,g5.book1.getData(2,i),g5.book1.getData(1,i));
            if (i==contentIndex) {
                g5.createTextBody(g5.book1.getData(3,i));
            }
        }
    } else {
        g5.create1();    
    }
    if (index==1) {
        g5.create2Active();
        g5.createTitle1("知识库：");
        for (i=0; i<g5.book2.rowCount; i++) {
            g5.createTitle2(i,g5.book2.getData(2,i));
            if (i==contentIndex) {
                g5.createTextBody(g5.book2.getData(3,i));
            }
        }
    } else {
        g5.create2();    
    }
    if (index==2) {
        g5.create3Active();
        g5.createTitle1("常见问题：");
        for (i=0; i<g5.book3.rowCount; i++) {
            g5.createTitle3(i,g5.book3.getData(2,i));
            if (i==contentIndex) {
                g5.createTextBody(g5.book3.getData(3,i));
            }
        }
    } else {
        g5.create3();    
    }

    eb.processDock();
    g5.root.scrollTop = scroll;        
}
g5.clearList = function() {
    while (g5.root.eeChildren.length > 0) {
        ee.removeControl(g5.root.eeChildren[0]);
    }
    g5.currentPos = 0;
}
g5.create1pj = function() {
    c = createPanel(g5.root, "temp", "EFEFEF", 0, 2, 450, 73);
    addText(c, "1陪驾", "center", "middle", "宋体", 9, "000000", "normal");
    eb.setFontSizeAuto(c, 15);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0;
    c.widthRatio=1;
    c.heightRatio=0.0998632010943912;
    c.topRatio=g5.currentPos;
    g5.currentPos+=c.heightRatio;
    c = createPanel(c, "temp", "808080", 0, 71, 450, 2);
    eb.setDock(c,"fill","bottom");    
}
