    c.topRatio=g5.currentPos;
    g5.currentPos+=c.heightRatio;
    var root = c;
    c = createPanel(root, "temp", "", 0, 241, 450, 2);
    eb.setDock(c,"fill","bottom");
    c = createPanel(root, "temp", "", 20, 20, 410, 203);
    c.style.overflow = "auto";
    eb.setOuter(c,20,20,20,20);
    addText(c, text, "left", "top", "宋体", 9, "7D7D7D", "normal");
    eb.setFontSizeAuto(c, 25);
    eb.setDock(c,"fill","fill");
}
