taskSetItem.script2:
var sql, sqlResult;  
//公告
sql = "SELECT id,convert(varchar,lastModify,20) as lastModify,title,[text] from news " +
    "where newsTypeStr = '系统公告' order by sort, lastmodify desc";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.clear();
    eio.appendString8("F");
    eio.appendString8("统繁忙，请稍后再试");
    es.logWarning("getNewsList数据库执行出错:" + sql);
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult, "id,lastModify,title,text",0,9999);
//知识库
sql = "SELECT id,convert(varchar,lastModify,20) as lastModify,title,[text] from news " +
    "where newsTypeStr = '知识库' order by sort, lastmodify desc";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.clear();
    eio.appendString8("F");
    eio.appendString8("统繁忙，请稍后再试");
    es.logWarning("getNewsList数据库执行出错:" + sql);
    return;
}
eio.appendDataSet(sqlResult, "id,lastModify,title,text",0,9999);
//常见问题
sql = "SELECT id,convert(varchar,lastModify,20) as lastModify,title,[text] from news " +
    "where newsTypeStr = '常见问题' order by sort, lastmodify desc";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.clear();
    eio.appendString8("F");
    eio.appendString8("统繁忙，请稍后再试");
    es.logWarning("getNewsList数据库执行出错:" + sql);
    return;
}
eio.appendDataSet(sqlResult, "id,lastModify,title,text",0,9999);

