e3.onclick=function(){
   if(e3.clk==false){
       ee.setRect(e1,null,null,null,100);
       ee.setRect(e2,null,100,null,1);
       ee.setRect(e3,null,101,null,100);
       ee.setRect(e4,null,201,null,120);
       ee.setRect(e5,null,321,null,100);
       ee.setRect(e6,null,421,null,1);
       e3.clk=true; 
       e1.clk=false; 
   }
   else {
       ee.setRect(e1,null,null,null,100);
       ee.setRect(e2,null,100,null,1);
       ee.setRect(e3,null,101,null,100);
       ee.setRect(e4,null,201,null,1);
       ee.setRect(e5,null,202,null,100);
       ee.setRect(e6,null,302,null,1); 
       e1.clk=false; 
       e3.clk=false;
   } 
}  

function firstPanel(h1,h2,h3){ // h1所有大标题标题高度,h2内容展开高度,h3内容收起高度 
    if(e1.clk==false){ 
       ee.setRect(e1,null,null,null,h1);
       ee.setRect(e2,null,h1,null,h2); 
       ee.setRect(e3,null,(h1+h2),null,h1); 
       ee.setRect(e4,null,(h1*2+h2),null,h3);
       ee.setRect(e5,null,(h1*2+h2)+h3,null,h1);
       ee.setRect(e6,null,(h1*3+h2+h3),null,h3); 
       e1.clk=true; 
       e3.clk=false;  
       }  
    else if(e3.clk==false){
       ee.setRect(e1,null,null,null,h1);
       ee.setRect(e2,null,h1,null,1);
       ee.setRect(e3,null,h1+h3,null,h1);
       ee.setRect(e4,null,h1*2+h3,null,h2);
       ee.setRect(e5,null,h1+h2+h3,null,h1);
       ee.setRect(e6,null,h1*2+h2+h3,null,h3);
       e3.clk=true; 
       e1.clk=false; 
    }   
    else {  
       ee.setRect(e1,null,null,null,h1);
       ee.setRect(e2,null,h1,null,h3); 
       ee.setRect(e3,null,(h1+h3),null,h1); 
       ee.setRect(e4,null,h1*2+h3,null,h3);
       ee.setRect(e5,null,h1*2+h3*2,null,h1);
       ee.setRect(e6,null,h1*3+h3*2,null,h3); 
       e1.clk=false; 
       e3.clk=false;
    }   
}
