//设计图中“知识库：”
g5.createTitle1 = function(name) {
    c = createPanel(g5.root, "temp", "E9E9E9", 0, 404, 450, 32);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0;
    c.widthRatio=1;
    c.heightRatio=0.0750875512995896;
    c.topRatio=g5.currentPos;
    g5.currentPos+=c.heightRatio;
    var root = c;
    c = createPanel(root, "temp", "", 28, 0, 422, 32);
    addText(c, name, "left", "middle", "黑体", 9, "7d7d7d", "normal");
    eb.setFontSizeAuto(c, 25);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0.0622222222222222;
    c.widthRatio=0.937777777777778;
    c.topRatio=0;
    c.heightRatio=1;
    c = createPanel(root, "temp", "cccccc", 0, 30, 490, 2);
    eb.setDock(c,"fill","bottom");
}
//设计图中“独立驾车训练”
g5.createTitle2 = function(index, name) {
    c = createPanel(g5.root, "temp", "FFFFFF", 0, 437, 450, 41);
    c.bookIndex = index;
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0;
    c.widthRatio=1;
    c.heightRatio=0.0750875512995896;
    c.topRatio=g5.currentPos;
    g5.currentPos+=c.heightRatio;
    var root = c;
    c = createPanel(root, "temp", "cccccc", 0, 39, 450, 1);
    eb.setDock(c,"fill","bottom");
    c = createPanel(root, "temp", "", 28, 0, 422, 41);
    addText(c, name, "left", "middle", "黑体", 9, "7D7D7D", "normal");
    eb.setFontSizeAuto(c, 25);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0.0622222222222222;
    c.widthRatio=0.937777777777778;
    c.topRatio=0;
    c.heightRatio=1;
    c = createPanel(c, "temp", "", 401, 16, 49, 11);
    ee.setImage(c,"/App1PJ/test/news/down.png","fill","","")
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0.872093023255814;
    c.widthRatio=0.106976744186047;
    c.topRatio=0.390243902439024;
