            dd.unshift(dd[dd.length-1])
            dd.pop();
        }
        var d= myArrToStr(dd);




function myArrToStr(arr){
    var str="";
    var tmp="";
    for(var i=0;i<arr.length;i++){
        if(i!=0){
            tmp =",";
        }else{
            tmp='';
        }
        if(arr[i]<10){
            tmp += "0"+arr[i];

        }else {
            tmp +=arr[i];
        }

        str+=tmp;
    }
    return str;

}
var Y = strY.toString();
var M = myArrToStr(strM);
var D = myArrToStr(strD);

g5.onStop = function(){

        var tmp = g5.r1.getSelectText();
        var year = g5.r.getSelectText();  
        var day = g5.r2.getSelectText();
       // alert(tmp);alert(year); alert(g5.r3.getSelectText()); 
       //alert(g5.r2.getSelectText());

       var days;
        if(tmp==1||tmp==3||tmp==5||tmp==7||tmp==8||tmp==10||tmp==12){  
            days=31
        }else if(tmp==2){
            if((year%4==0&amp;&amp;year%100!=0)||(year%400==0)){//闰年
               days=29
            }else {
               days=28;
            }
        }else {                
           days=30;
        } 
        var dd =[];
        for(var i=1;i<=days;i++){
            dd.push(i);
        }
        for(var i=0;i<day-2;i++){  

            dd.push(dd[0]);
            dd.shift();    

        }  
        if(day ==1){
            dd.unshift(dd[dd.length-1])
            dd.pop();
        }
        var d= myArrToStr(dd);

        g5.r2 = new g5.rollY(ee.getControl("panel151"), d,false);

}

