g5.createP = function(num){ 
   //alert("c")
    var top = (595 + (num-1)*220)*(document.body.scrollHeight/816);
    var tR = top/(document.body.scrollHeight);  
    //console.log("tR="+tR);
    //console.log(document.body.scrollHeight); 
    var b = document.body.scrollHeight/816;

    c = createPanel($g("ee_divRoot"), "tmp", "", 9*b, top, 428*b, 218*b);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0.0202702702702703;
    c.widthRatio=0.963963963963964;
    c.topRatio=tR;
    c.heightRatio=0.267156862745098;
    var root =c;
    c = createPanel(root, "tmp", "", 0, 0, 428, 50);  
    var title ="";
     switch(num){
        case 1: title="第一辆车";break;
        case 2: title="第二辆车";break;
        case 3: title="第三辆车";break;
        case 4: title="第四辆车";break;
        case 5: title="第五辆车";break;
        case 6: title="第六辆车";break;
        case 7: title="第七辆车";break;
        case 8: title="第八辆车";break;
        default : title = "第一辆车";break;
    }  
    addText(c, title, "center", "middle", "黑体", 9, "505050", "normal");
    eb.setFontSizeAuto(c, 25);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0;
    c.widthRatio=1;
    c.topRatio=0;
    c.heightRatio=0.229357798165138;
    var root1 = c;   
    c = createPanel(root1, "tmp", "", 381, 10, 25, 25);
    ee.setImage(c,"/App1PJ/student/apply/del2.png","fill","","")
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0.89018691588785;
    c.widthRatio=25/441;
    c.topRatio=0.2;
    c.heightRatio=25/50; 
    ee.setOnClick(c,function(sender,event) {
        var cell = sender.parentNode.parentNode;

        g5.carNum -=1;
        console.log("carNum="+g5.carNum);
        var b =document.body.scrollHeight/816; 
        var tmp = cell; 
        var old = cell;

        var count =0; 
        //alert(1)
        while(1){
            //alert(2)
           tmp = tmp.nextElementSibling; 
           console.log(tmp);
           if (tmp == null){
               break;
           }
           count+=1;
           console.log("count="+count); 
           //alert(3)  

        } 

        while(1){
            cell = cell.nextElementSibling;   
            ee.setRect(ee.getControl("panel89"),0,(694+222*(g5.carNum))*b,441*b,125*b);   
            ee.setRect(ee.getControl("panel16"),58*b,(610+222*(g5.carNum))*b,331*b,41*b); 
            //eb.processDock();                   
            if(cell == null){
                break;
            } 
            var t = g5.carNum-count; 

            //ee.setRect(cell,9*b,(595 + (t-1)*220)*(document.body.scrollHeight/816) ,441*b,218*b); 
            cell.topRatio = (595 + (t)*220)*(document.body.scrollHeight/816)/document.body.scrollHeight;
            switch(t+1){
                case 1: title="第一辆车";break;
                case 2: title="第二辆车";break;
                case 3: title="第三辆车";break;
                case 4: title="第四辆车";break;
                case 5: title="第五辆车";break;
                case 6: title="第六辆车";break;
                case 7: title="第七辆车";break;
                case 8: title="第八辆车";break;
                default : title = "第一辆车";break;
            }
            ee.setText(cell.eeChildren[0],title)
            count--;
            console.log("countDD="+count);  
            eb.processDock();

        } 
        console.log("countRR="+count);  

        ee.removeControl(old);

        //eb.processDock();

    });
