var cell;

cell=ee.getControl("divRoot").eeChildren[g5.chooseCar];
 g5.car1 ={};
var band = cell.eeChildren[1].eeChildren[1].eeChildren[1]; 
g5.car1.band = ee.getEditText(band);

var number = cell.eeChildren[2].eeChildren[1].eeChildren[1];
g5.car1.number = ee.getEditText(number);

var p = cell.eeChildren[3].eeChildren[1].eeChildren[1]; 
var p1 = cell.eeChildren[3].eeChildren[1].eeChildren[4];

g5.car1.type =""; 
g5.car1.f1 ="";
while(1==1){
    var src = p.img.src;
    var index = src.lastIndexOf("/");
    var str = src.substring(index+1);
    if (str == "checked.png"){
        g5.car1.type = "自动挡";
        break;
    } 
    var src = p1.img.src;
    var index = src.lastIndexOf("/");
    var str = src.substring(index+1);
    if (str == "checked.png"){
        g5.car1.type = "手动挡";
        break;
    } 
    g5.car1.f1="kong"     
    break;
}  

//variety 多样的 
var v1 = cell.eeChildren[4].eeChildren[1].eeChildren[1];  
var v2 = cell.eeChildren[4].eeChildren[1].eeChildren[3]; 
var v3 = cell.eeChildren[4].eeChildren[1].eeChildren[5]; 

g5.car1.variety = "";  
g5.car1.f2 ="";
while(1){
    var src = v1.img.src;
    var index = src.lastIndexOf("/");
    var str = src.substring(index+1);
    if (str == "checked.png"){
        g5.car1.variety = "两厢车";
        break;
    }
    var src = v2.img.src;
    var index = src.lastIndexOf("/");
    var str = src.substring(index+1);
    if (str == "checked.png"){
        g5.car1.variety = "三厢车";
        break;
    }
    var src = v3.img.src;
    var index = src.lastIndexOf("/");
    var str = src.substring(index+1);
    if (str == "checked.png"){
        g5.car1.variety = "SUV";
        break;
    }
    g5.car1.f2 ="kong";
    break; 
}

