var tt = parseInt(ee.getText(ee.getControl("layoutxitem2")));   
              //tt == 60
function smstext(){     
    if(tt == 0){
        clearInterval(smstt);  
        console.log("tt=0");
        ee.setText(ee.getControl("layoutxitem2"),"获取短信验证码"); 
        return;
    }else if (tt>0) {  
        tt--;            
        console.log(tt);
        ee.setText(ee.getControl("layoutxitem2"),tt+"秒后获取");
    }else {           
        console.log("错误"+tt);   
        clearInterval(smstt);
        ee.setText(ee.getControl("layoutxitem2"),"获取短信验证码"); 
    } 

}

