taskSetItem.script2:
var keyword=eio.readString8();
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var condition = " where 1=1 " 
if (keyword !=''){
    condition += " and concat(id,username,note) like '%"+db.encode(keyword)+"%'"; 
}


var sql,sqlResult;
sql = "select count(*) as c from adminUser"+ condition ;
sqlResult = db.executeQuery("PJ",sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult,"c");
eio.appendInt32(count);


sql = "select * from adminUser " +condition;  
eio.appendString8(sql);
sqlResult = db.executeQuery("PJ",sql);

eio.appendDataSet(sqlResult,"id,username,password,privilege,regDatetime,note", pageSize*(pageCurrent-1), pageSize);

