var userName = eio.readString8(); 
var pwd = eio.readString8();
var lv = eio.readString8();
var note = eio.readString8();
var pwdMd5 = es.getMd5(pwd);

var sql,sqlResult; 
sql = "select username,id from adminUser where username='"+db.encode(userName)+"'";
sqlResult = db.executeQuery("PJ",sql);
db.read(sqlResult);
var nowName = db.getString(sqlResult,sql);
if(nowName != ""){
    eio.appendString8("F");
    eio.appendString8("用户名已存在");
    return;     
} 
//处理时间 
var now = new Date();
sql = "insert into adminUser values('"+db.encode(now)+"', '"+db.encode(userName)+"', '"+db.encode(pwdMd5)+"', '"+db.encode(note)+"', '"+db.encode(lv)+"')" ;
sqlResult = db.executeNoneQuery("PJ",sql);  
if(sqlResult == null){
    eio.appendString8("F");
    eio.appendString8("添加失败");
}
eio.appendString8("T");

eio.appendString8(sql);

