taskSetItem.script2:
var id = eio.readString8();   
var userName = eio.readString8();
var pwd = eio.readString8();
var lv = eio.readString8(); 
var note = eio.readString8(); 


//查询判断密码是否修改
sql = "select password from adminUser where id='"+db.encode(id)+"'";
sqlResult = db.executeQuery("PJ",sql);
db.read(sqlResult);
var nowPwd = db.getString(sqlResult,"password");
if(nowPwd == pwd){
    var pwdMd5 = pwd;
}else {
    var pwdMd5 = es.getMd5(pwd);
} 
//判断username 是否可用  
sql = "select username,id from adminUser where username='"+db.encode(userName)+"'";
sqlResult = db.executeQuery("PJ",sql);     
db.read(sqlResult);
var nowUsername = db.getString(sqlResult,"username"); 
var nowId = db.getString(sqlResult,"id");
if (nowUsername != "" && nowId !=id){
    eio.appendString8("F1");
    eio.appendString8("账户名已存在");
    return;

}   

var now = new Date();
var sql,sqlResult;
sql="update adminUser set username='"+db.encode(userName)+"', password='"+db.encode(pwdMd5)+
    "', privilege='"+db.encode(lv)+"', note='"+db.encode(note)+ "',regDatetime='"+db.encode(now)+
    "' where id='"+db.encode(id)+"'";
sqlResult = db.executeNoneQuery("PJ",sql);


eio.appendString8("T"); 
eio.appendString8("");
eio.appendString8(sql);

