control.onUpdate:
if(text.length > 0){
    if (text == 'VCN') {
        text = '认证申请';
        ee.setText(sender, text);
        return;
    }

   // if (text == 'VNN') {
   //     text = '认证失败';
   //     ee.setText(sender, text);
   //     return;
   // }  

    if (text == 'VVN') {
        text = '已认证';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'VNN') {
        text = '已审核';
        ee.setText(sender, text);
        return;
    }

    if (text == 'NNN') {
        text = '审核失败';
        ee.setText(sender, text);
        return;
    }


    if (text == 'CNN') {
        text = '审核申请';
        ee.setText(sender, text);
        return;
    }
    if (text == 'VVV') {
        text = '已测试';
        ee.setText(sender, text);
        return;
    }

    if (text == 'NNN') {
        text = '审核失败';
        ee.setText(sender, text);
        return;
    }

    //if (text == 'VVC') {
    //    text = '未测试';
    //    ee.setText(sender, text);
    //    return;
    //}

}else{
    text = ''; 
    ee.setText(sender,text);
}

