control.onUploadStartC:
//初步过滤
fileExt = fileExt.toUpperCase();
if ((fileExt!=".JPG")&&(fileExt!=".BMP")&&(fileExt!=".PNG")&&(fileExt!=".JPEG")) {
    alert("对不起，请务必选择一张有效的图片"+fileExt);
    return;
}
ee.showAni(ee.getControl("panel20"));
g5.setProgress(0.06);

control.onUploadStartS:
//产生文件名
var sql = 
    "declare @r varchar(20); " +
    "exec @r = getPhotoName; " +
    "select @r as result;";
var sqlResult = db.executeQuery("PJ", sql);
if (fileExt.length<=0) {
    fileExt = ".jpg";
}
while (true) {
    if (sqlResult == null) {
        cancelUpload = true;
        errorString = "系统繁忙，请稍后再试";
        break;
    }
    db.read(sqlResult);
    newFileName = db.getString(sqlResult, "result") + fileExt.toLowerCase();
    newFileName = "E:\\www\\root\\userRes\\" + newFileName;
    break;
}

