taskSetItem.script2:
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var e1 = eio.readString16();
//var e2 = eio.readString16();
var e3 = eio.readInt32();
var e4 = eio.readInt32();
var e5 = eio.readInt32();
var e6 = eio.readInt32();
var e7 = eio.readInt32();
var e8 = eio.readInt32();
//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "where userType='教练'";
if (e1!="") {
    condition += " and concat(nickName,idName,mobile) like '%" + db.encode(e1) + "%'";
}
//if (e2>0) {
//    condition += " and mobile = " + e2;
//}

if (e3!=0) {
    condition += " and autoStatus = 'VVN'";
}
if (e4!=0) {
    condition += " and autoStatus = 'VNN'";
}
if (e5!=0) {
    condition += " and autoStatus = 'VVV'";
}
if (e6!=0) {
    condition += " and autoStatus = 'VCN'";
}
if (e7!=0) {
    condition += " and autoStatus = 'CNN'";
}
if (e8!=0) {
    condition += " and autoStatus = 'NNN'";
}


var sql, sqlResult;

//获取记录数量
sql = "select count(*) as c from userInfo " + condition;
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表
sql =
    "SELECT id,nickName,mobile,idName,idNumber," +
    "autoStatus,photo2,photo3,photo5,photo6,ISNULL(photo7,'')+'`'+ISNULL(licenseNumber,'')+'`'+ISNULL(carId,'')+'`'+ISNULL(carType,'')+'`'+ISNULL(address,'')+'`'+ISNULL(serviceArea,'') as data"+
    " from userInfo " +
    condition +"order by id desc ";  
es.logWarning(sql);    
sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult, "id,nickName,mobile,idName,autoStatus,photo2,photo3,photo5,photo6,data", pageSize*(pageCurrent-1), pageSize);

