    sqlResult=db.executeNoneQuery("PJ", sql) 
    db.read(sqlResult) 
    es.logWarning(sql);
    if (sqlResult == null) {
        eio.appendString8("F");
        eio.appendString8("系统繁忙请稍后再试");
        return; 
        }
    sql = "insert into sendNews (userId,title,newsContent,status) values (" +Id + ",'审核通过通知','"+ db.encode(covenent) + "','未读')";  
