    applyStatus = "VVN";   
    var covenent="尊敬的教练：您提交的资料已通过认证，请登录客户端查看！";
    sql = "exec sendSms '" + db.encode(mobile) + "','"+ db.encode(covenent) + "'"
    sqlResult=db.executeNoneQuery("PJ", sql) 
    db.read(sqlResult) 
    es.logWarning(sql);
    if (sqlResult == null) {
        eio.appendString8("F");
        eio.appendString8("系统繁忙请稍后再试");
        return; 
        }
    sql = "insert into sendNews (userId,title,newsContent,status) values (" +Id + ",'认证通过通知','"+ db.encode(covenent) + "','未读')";  
    sqlResult=db.executeNoneQuery("PJ", sql)   
    es.logWarning(sql);

}
if(applyStatus == "已认证"){ //未测试
    applyStatus = "VVV";
}                       

sql = 
        "update userInfo set " +
        "autoStatus='" + db.encode(applyStatus) + "'" +
        "where id=" + Id;
