//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "where 1=1";
if (e1!="") {
    e1 = e1.replace(/-/g,"/"); 
    e1 = e1.replace(/：/g,":");
    e1 = e1.replace(/(^\s*)|(\s*$)/g, ""); //去除字符串左右两边空格;
    //es.logWarning("bankcard query")
    //es.logWarning("e1="+e1);
    var index = e1.indexOf("/"); 
    var start,end;
    var date= "";
    if(index != -1){ 
        var index1 = e1.indexOf(" ");
        if (index1 != -1 ){
            date = e1.substr(0,index1);
            end = date + " 23:59:59";
            start = e1;
        }else {
            var dArr = e1.split("/"); 
            //es.logWarning("dArr[].length ="+dArr.length);

            if (dArr.length == 3){ 
                start = e1;
                end = e1;

            }else if (dArr.length ==2){
                if (dArr[0] != "" &amp;&amp; dArr[1] != ""){
                    if(dArr[0] > 12){
                        if (dArr[0].length == 2){
                            e1 = "20"+ e1;
                            dArr[0] = "20"+dArr[0];
                        }
                        start= e1 + "/01";                          
                        end = e1 + "/" + getEndOfMonth(dArr[0],dArr[1]);
                    }else {
                        start = getYear() +"/"+ e1;
                        end = getYear() +"/"+ e1;
                    }
                }
            } 
            start += " 00:00:00";
            end += " 23:59:59";
        }           
        //es.logWarning("start="+start);
        //es.logWarning("end="+end); 
        condition += " and bankCard.regDatetime between '"+db.encode(start)+"' and '"+db.encode(end)+"'";
    }else {
        condition += " and concat([userInfo].nickName,[userInfo].idName,[userInfo].mobile,[bankCard].cardUserName,[bankCard].cardNumber,bankName,[bankCard].regDatetime) like '%" + db.encode(e1) + "%'";
    }
}

if(nickName!=""){
    //condition +=" and bankCard.userId = " + userId ;
    //获取用ID
    sql  = "select id as c from userInfo where nickName='" + nickName + "'";
    //es.logWarning(sql);
    sqlResult= db.executeQuery("PJ", sql);
    db.read(sqlResult);
    var userId = db.getString(sqlResult, "c");   
    //es.logWarning(userId); 
    condition +=" and bankCard.userId = " + userId ;
}

