taskSetItem.script2:
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var e1 = eio.readString16();  
var e2 = eio.readInt32();
var e3 = eio.readInt32();
var e4 = eio.readInt32();


//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "  where ([sendNews].status='已读' or [sendNews].status='未读'or [sendNews].status='删除') ";
//es.logWarning(condition);  
if(e2!=0){                              
    condition =" where [sendNews].status ='未读'";
}  
if(e3!=0){
   condition =" where [sendNews].status ='已读'";
}
if(e4!=0){
   condition =" where [sendNews].status ='删除'";
}
if (e1!="") {
    condition += " and concat(userInfo.nickName,userInfo.mobile,userInfo.idName,userInfo.userType,sendNews.status,sendNews.title,sendNews.newsContent) like '%" + db.encode(e1) + "%'";
}   
var sql, sqlResult;    

//获取记录数量
sql = "select count(*) as c from sendNews left outer join userInfo on sendNews.userId = userInfo.id "+condition; 
es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表 
sql =
    "select [sendNews].id,[sendNews].regDatetime,[sendNews].title,[sendNews].newsContent,[sendNews].status,[sendNews].userId , " +
    "[userInfo].nickName ,[userInfo].mobile,[userInfo].idName,[userInfo].userType " +
    "from [sendNews] LEFT OUTER JOIN userInfo ON [sendNews].userId = userInfo.id " + condition + " order by regDatetime desc ";

es.logWarning(sql);    
var sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult,"id,nickName,idName,mobile,userType,regDatetime,status,title,newsContent", pageSize*(pageCurrent-1), pageSize);
es.logWarning("success11111111");

/*
    if (sqlResult == null) {
        eio.appendString8("F");
        eio.appendString8("系统繁忙，请稍后再试");
        break;
    }
    eio.appendString8("T");
    eio.appendDataSet(sqlResult, "id,nickName,regDatetime,intParam1,intParam2,note", pageSize*(pageCurrent-1), pageSize);  
