taskSetItem.script2:
var title = eio.readString16();  //群发标题
var content = eio.readString16();  //群发内容
var userType = eio.readString16();  //用户类型
var sql, sqlResult;

if(userType=="0"){
    userType='教练'
}else if(userType=="1") {
    userType='学员'
}

sql = "exec massNews '"+ db.encode(title) +"' ,'"+ db.encode(content) + "' , '"+ db.encode(userType) +"'";
es.logWarning(sql);

if(sqlResult = db.executeQuery("PJ", sql)){
    eio.appendString8("T");
}else{
    eio.appendString8("F");
}

//eio.appendString8("T");
es.logWarning("success");

